// Enhanced LazyChart.js - Works with real forecast data from backend

import React, { useEffect, useRef } from 'react';

const LazyChart = ({ dashboardData, selectedParameter }) => {
  const chartRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    // Only initialize if Chart.js is available
    if (!window.Chart) {
      console.log('âŒ Chart.js not available');
      return;
    }

    const initChart = () => {
      try {
        // Clean up existing chart
        if (chartRef.current) {
          chartRef.current.destroy();
          chartRef.current = null;
        }

        if (!canvasRef.current) {
          console.log('âŒ Canvas ref not available');
          return;
        }

        // Get forecast data from backend structure
        let forecastData = [];
        
        // Try to get forecast data from different possible locations in the response
        if (dashboardData?.forecast_data?.nearest_station) {
          forecastData = dashboardData.forecast_data.nearest_station;
        } else if (dashboardData?.forecast_data?.all_stations) {
          // If we have all stations data, use the first available
          const stationKeys = Object.keys(dashboardData.forecast_data.all_stations);
          if (stationKeys.length > 0) {
            forecastData = dashboardData.forecast_data.all_stations[stationKeys[0]] || [];
          }
        } else if (dashboardData?.all_stations_data) {
          // Try to get forecast from all_stations_data
          const selectedDevice = dashboardData.selected_device || 'lora_v1';
          if (dashboardData.all_stations_data[selectedDevice]?.forecast_data) {
            forecastData = dashboardData.all_stations_data[selectedDevice].forecast_data;
          }
        }

        // Fallback if no forecast data found
        if (!forecastData || forecastData.length === 0) {
          console.log('âŒ No forecast data available');
          forecastData = [
            { day: 'Today', pm25_max: 25, pm10_max: 42, so2_max: 8, no2_max: 40, co_max: 1.1, o3_max: 46, nh3_max: 93 },
            { day: 'Tomorrow', pm25_max: 24, pm10_max: 34, so2_max: 8, no2_max: 45, co_max: 1.2, o3_max: 43, nh3_max: 100 },
            { day: 'Day 3', pm25_max: 24, pm10_max: 30, so2_max: 8, no2_max: 42, co_max: 1.2, o3_max: 45, nh3_max: 100 },
            { day: 'Day 4', pm25_max: 20, pm10_max: 23, so2_max: 8, no2_max: 38, co_max: 1.2, o3_max: 47, nh3_max: 100 }
          ];
        }

        console.log('ðŸ“Š Using forecast data:', forecastData);
        console.log('ðŸ“Š Selected parameter:', selectedParameter);

        // Extract data for the selected parameter
        const data = forecastData.map(item => {
          const paramKey = `${selectedParameter}_max`;
          const value = item[paramKey] || 0;
          return parseFloat(value);
        });

        const labels = forecastData.map(item => item.day || 'Unknown');

        console.log('ðŸ“Š Chart data:', data);
        console.log('ðŸ“Š Chart labels:', labels);

        // Get parameter-specific styling
        const getParameterColor = (param) => {
          const colors = {
            pm25: '#10b981',    // Green
            pm10: '#3b82f6',    // Blue
            so2: '#f59e0b',     // Yellow
            no2: '#ef4444',     // Red
            co: '#8b5cf6',      // Purple
            o3: '#06b6d4',      // Cyan
            nh3: '#f97316'      // Orange
          };
          return colors[param] || '#6b7280';
        };

        const paramColor = getParameterColor(selectedParameter);

        // Create chart
        const ctx = canvasRef.current.getContext('2d');
        chartRef.current = new window.Chart(ctx, {
          type: 'line',
          data: {
            labels: labels,
            datasets: [{
              label: `${selectedParameter.toUpperCase()} Forecast`,
              data: data,
              borderColor: paramColor,
              backgroundColor: `${paramColor}20`, // 20% opacity
              tension: 0.4,
              fill: true,
              pointBackgroundColor: paramColor,
              pointBorderColor: '#ffffff',
              pointBorderWidth: 2,
              pointRadius: 6,
              pointHoverRadius: 8,
              borderWidth: 3
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            interaction: {
              intersect: false,
              mode: 'index'
            },
            plugins: {
              legend: {
                display: true,
                position: 'top',
                labels: {
                  usePointStyle: true,
                  padding: 20,
                  font: {
                    size: 12,
                    weight: 600
                  }
                }
              },
              tooltip: {
                backgroundColor: 'rgba(255, 255, 255, 0.95)',
                titleColor: '#1f2937',
                bodyColor: '#4b5563',
                borderColor: paramColor,
                borderWidth: 1,
                cornerRadius: 8,
                displayColors: false,
                callbacks: {
                  title: function(context) {
                    return `${context[0].label}`;
                  },
                  label: function(context) {
                    const unit = selectedParameter === 'co' ? 'mg/mÂ³' : 'Âµg/mÂ³';
                    return `${selectedParameter.toUpperCase()}: ${context.parsed.y.toFixed(1)} ${unit}`;
                  }
                }
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                grid: {
                  color: 'rgba(0, 0, 0, 0.1)',
                  lineWidth: 1
                },
                ticks: {
                  color: '#6b7280',
                  font: {
                    size: 11
                  },
                  callback: function(value) {
                    return Math.round(value);
                  }
                },
                title: {
                  display: true,
                  text: `Concentration (${selectedParameter === 'co' ? 'mg/mÂ³' : 'Âµg/mÂ³'})`,
                  color: '#4b5563',
                  font: {
                    size: 12,
                    weight: 600
                  }
                }
              },
              x: {
                grid: {
                  color: 'rgba(0, 0, 0, 0.05)',
                  lineWidth: 1
                },
                ticks: {
                  color: '#6b7280',
                  font: {
                    size: 11,
                    weight: 600
                  }
                }
              }
            },
            elements: {
              line: {
                tension: 0.4
              },
              point: {
                hoverBorderWidth: 3
              }
            }
          }
        });

        console.log('âœ… Forecast chart initialized successfully');

      } catch (error) {
        console.error('âŒ Forecast chart initialization failed:', error);
        
        // Show error message in canvas
        if (canvasRef.current) {
          const ctx = canvasRef.current.getContext('2d');
          ctx.fillStyle = '#ef4444';
          ctx.font = '14px Arial';
          ctx.textAlign = 'center';
          ctx.fillText('Chart loading failed', canvasRef.current.width / 2, canvasRef.current.height / 2);
        }
      }
    };

    // Initialize chart with a small delay
    setTimeout(initChart, 100);

    return () => {
      if (chartRef.current) {
        try {
          chartRef.current.destroy();
        } catch (e) {
          console.log('Error destroying chart:', e);
        }
        chartRef.current = null;
      }
    };
  }, [dashboardData, selectedParameter]);

  return (
    <div className="chart-container" style={{ height: '200px', marginTop: '15px' }}>
      <canvas 
        ref={canvasRef}
        style={{ 
          width: '100%', 
          height: '100%',
          background: 'white',
          borderRadius: '8px',
          border: '1px solid #e2e8f0'
        }}
      />
    </div>
  );
};

export default LazyChart;
