import React, { useState, useEffect } from 'react';
import './css/HealthAssessment.css';

function HealthAssessment() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [formData, setFormData] = useState({
    age_group: '',
    gender: '',
    respiratory_conditions: [],
    smoking_history: '',
    living_environment: [],
    common_symptoms: [],
    occupational_exposure: '',
    medical_history: []
  });
  const [healthScore, setHealthScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [username, setUsername] = useState('');

  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // Get username from localStorage or session
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    setUsername(user.name || 'User');
  }, []);

  const questions = [
    {
      id: 'age_group',
      title: 'What is your age group?',
      icon: 'fas fa-user-clock',
      type: 'radio',
      options: [
        { value: '0-12 years', label: '0-12 years' },
        { value: '13-18 years', label: '13-18 years' },
        { value: '19-40 years', label: '19-40 years' },
        { value: '41-60 years', label: '41-60 years' },
        { value: '61 years and above', label: '61 years and above' }
      ]
    },
    {
      id: 'gender',
      title: 'What is your gender?',
      icon: 'fas fa-venus-mars',
      type: 'radio',
      options: [
        { value: 'Male', label: 'Male' },
        { value: 'Female', label: 'Female' },
        { value: 'Other', label: 'Other' },
        { value: 'Prefer not to say', label: 'Prefer not to say' }
      ]
    },
    {
      id: 'respiratory_conditions',
      title: 'Do you have any respiratory conditions?',
      icon: 'fas fa-lungs',
      type: 'checkbox',
      options: [
        { value: 'Asthma', label: 'Asthma' },
        { value: 'COPD', label: 'COPD' },
        { value: 'Chronic Bronchitis', label: 'Chronic Bronchitis' },
        { value: 'Emphysema', label: 'Emphysema' },
        { value: 'None', label: 'None of the above' }
      ]
    },
    {
      id: 'smoking_history',
      title: 'Smoking History',
      icon: 'fas fa-smoking',
      type: 'radio',
      options: [
        { value: 'Never smoked', label: 'Never smoked' },
        { value: 'Former smoker', label: 'Former smoker' },
        { value: 'Current smoker', label: 'Current smoker' },
        { value: 'Exposed to secondhand smoke', label: 'Exposed to secondhand smoke' }
      ]
    },
    {
      id: 'living_environment',
      title: 'Living Environment',
      icon: 'fas fa-city',
      type: 'checkbox',
      options: [
        { value: 'Urban area', label: 'Urban area with heavy traffic' },
        { value: 'Industrial zone', label: 'Near industrial zone' },
        { value: 'Rural area', label: 'Rural area' },
        { value: 'Coastal area', label: 'Coastal area' }
      ]
    },
    {
      id: 'common_symptoms',
      title: 'Common Symptoms',
      icon: 'fas fa-heartbeat',
      type: 'checkbox',
      options: [
        { value: 'Frequent coughing', label: 'Frequent coughing' },
        { value: 'Shortness of breath', label: 'Shortness of breath' },
        { value: 'Wheezing', label: 'Wheezing' },
        { value: 'Chest tightness', label: 'Chest tightness' },
        { value: 'None', label: 'None of the above' }
      ]
    },
    {
      id: 'occupational_exposure',
      title: 'Occupational Exposure',
      icon: 'fas fa-briefcase',
      type: 'radio',
      options: [
        { value: 'Construction/Mining', label: 'Construction/Mining' },
        { value: 'Chemical Industry', label: 'Chemical Industry' },
        { value: 'Healthcare', label: 'Healthcare' },
        { value: 'Agriculture', label: 'Agriculture' },
        { value: 'Office Environment', label: 'Office Environment' },
        { value: 'Other', label: 'Other' }
      ]
    },
    {
      id: 'medical_history',
      title: 'Medical History',
      icon: 'fas fa-notes-medical',
      type: 'checkbox',
      options: [
        { value: 'Hypertension', label: 'Hypertension' },
        { value: 'Diabetes', label: 'Diabetes' },
        { value: 'Heart Disease', label: 'Heart Disease' },
        { value: 'Allergies', label: 'Allergies' },
        { value: 'Immunocompromised', label: 'Immunocompromised' }
      ]
    }
  ];

  const handleOptionChange = (questionId, value, isCheckbox = false) => {
    if (isCheckbox) {
      setFormData(prev => {
        const currentValues = prev[questionId] || [];
        
        if (value === 'None') {
          // If "None" is selected, clear all other selections
          return {
            ...prev,
            [questionId]: currentValues.includes('None') ? [] : ['None']
          };
        } else {
          // Remove "None" if other options are selected
          const filteredValues = currentValues.filter(v => v !== 'None');
          
          if (filteredValues.includes(value)) {
            return {
              ...prev,
              [questionId]: filteredValues.filter(v => v !== value)
            };
          } else {
            return {
              ...prev,
              [questionId]: [...filteredValues, value]
            };
          }
        }
      });
    } else {
      setFormData(prev => ({
        ...prev,
        [questionId]: value
      }));
    }
  };

  const isQuestionAnswered = (questionIndex) => {
    const question = questions[questionIndex];
    const value = formData[question.id];
    
    if (question.type === 'checkbox') {
      return Array.isArray(value) && value.length > 0;
    } else {
      return value !== '';
    }
  };

  const nextQuestion = () => {
    if (isQuestionAnswered(currentQuestion)) {
      if (currentQuestion < questions.length - 1) {
        setCurrentQuestion(currentQuestion + 1);
        setError('');
      }
    } else {
      setError('Please select an option before proceeding');
      setTimeout(() => setError(''), 3000);
    }
  };

  const prevQuestion = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
      setError('');
    }
  };

  const calculateHealthScore = () => {
    let score = 0;

    // Age Group scoring
    const ageScores = {
      "0-12 years": 5,
      "13-18 years": 8,
      "19-40 years": 10,
      "41-60 years": 15,
      "61 years and above": 20
    };
    score += ageScores[formData.age_group] || 0;

    // Gender scoring
    score += formData.gender === "Male" ? 2 : 1;

    // Respiratory conditions
    if (formData.respiratory_conditions && !formData.respiratory_conditions.includes("None")) {
      score += formData.respiratory_conditions.length * 3;
    }

    // Smoking history
    const smokingScores = {
      "Never smoked": 0,
      "Former smoker": 10,
      "Current smoker": 25,
      "Exposed to secondhand smoke": 8
    };
    score += smokingScores[formData.smoking_history] || 0;

    // Living environment
    const environmentScores = {
      "Urban area": 10,
      "Industrial zone": 15,
      "Rural area": 3,
      "Coastal area": 2
    };
    if (formData.living_environment) {
      formData.living_environment.forEach(env => {
        score += environmentScores[env] || 0;
      });
    }

    // Common symptoms
    const symptomScores = {
      "Frequent coughing": 8,
      "Shortness of breath": 10,
      "Wheezing": 8,
      "Chest tightness": 9
    };
    if (formData.common_symptoms) {
      formData.common_symptoms.forEach(symptom => {
        score += symptomScores[symptom] || 0;
      });
    }

    // Occupational exposure
    const occupationScores = {
      "Construction/Mining": 15,
      "Chemical Industry": 15,
      "Healthcare": 8,
      "Agriculture": 10,
      "Office Environment": 3,
      "Other": 5
    };
    score += occupationScores[formData.occupational_exposure] || 0;

    // Medical history
    const conditionScores = {
      "Hypertension": 8,
      "Diabetes": 8,
      "Heart Disease": 10,
      "Allergies": 5,
      "Immunocompromised": 12
    };
    if (formData.medical_history) {
      formData.medical_history.forEach(condition => {
        score += conditionScores[condition] || 0;
      });
    }

    return score;
  };

  const getHealthStatus = (score) => {
    if (score <= 50) {
      return {
        status: 'Excellent Health',
        color: '#4CAF50',
        message: 'You are in excellent health. Keep up your healthy lifestyle!'
      };
    } else if (score <= 80) {
      return {
        status: 'Good Health',
        color: '#8BC34A',
        message: 'You are in good health. Continue to maintain your current lifestyle.'
      };
    } else if (score <= 120) {
      return {
        status: 'Moderate Risk',
        color: '#9C27B0',
        message: 'Some areas need attention. Consider improving your health habits.'
      };
    } else if (score <= 150) {
      return {
        status: 'Warning',
        color: '#FF9800',
        message: 'Some areas require serious attention. We recommend consulting a healthcare professional.'
      };
    } else if (score <= 200) {
      return {
        status: 'High Risk',
        color: '#F44336',
        message: 'High health risks detected. Please seek immediate medical advice.'
      };
    } else {
      return {
        status: 'Critical Risk',
        color: '#B71C1C',
        message: 'Severe health risks detected. Immediate medical intervention is necessary.'
      };
    }
  };

  const handleSubmit = async () => {
    if (!isQuestionAnswered(currentQuestion)) {
      setError('Please answer all questions before submitting');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const score = calculateHealthScore();
      
      const response = await fetch(`${API_BASE_URL}/api/health-assessment/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          health_score: score,
          username: username
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setHealthScore(score);
        setShowResult(true);
        
        // Redirect to dashboard after 3 seconds
        setTimeout(() => {
          window.location.href = '/dashboard';
        }, 3000);
      } else {
        setError(data.error || 'Failed to save health assessment');
      }
    } catch (err) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const progressPercentage = ((currentQuestion + 1) / questions.length) * 100;

  if (showResult) {
    const healthStatus = getHealthStatus(healthScore);
    
    return (
      <div className="health-assessment-page">
        <div className="navbar">
          <div className="navbar-content">
            <a href="/" className="navbar-brand">
              <img src="/aqi.webp" alt="AQM Logo" />
              AirAware
            </a>
          </div>
        </div>

        <div className="container">
          <div className="result-container">
            <div className="result-header">
              <h1><i className="fas fa-check-circle"></i> Assessment Complete!</h1>
              <p>Hello, {username}! Your health assessment has been completed.</p>
            </div>

            <div className="health-score-display">
              <div className="score-circle">
                <div className="score-inner-circle">
                  {healthScore}
                </div>
              </div>
              <h2>Your Health Score: {healthScore}</h2>
              <div className="health-status" style={{ color: healthStatus.color }}>
                {healthStatus.status}
              </div>
              <p className="health-message">{healthStatus.message}</p>
            </div>

            <div className="redirect-message">
              <i className="fas fa-arrow-right"></i>
              <p>Redirecting to your dashboard in 3 seconds...</p>
            </div>

            <div className="action-buttons">
              <button 
                onClick={() => window.location.href = '/dashboard'}
                className="btn-primary"
              >
                Go to Dashboard Now
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestionData = questions[currentQuestion];

  return (
    <div className="health-assessment-page">
      <div className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" />
            AirAware
          </a>
        </div>
      </div>

      <div className="container">
        <div className="header">
          <h1><i className="fas fa-notes-medical"></i> Health Assessment Questionnaire</h1>
          <p>Hello, {username}! Let's evaluate your health status.</p>
          <div className="back-button-container">
            <a href="/dashboard" className="back-button">
              <i className="fas fa-arrow-left"></i> Back to Profile
            </a>
          </div>
        </div>

        <div className="progress-container">
          <div className="progress-stats">
            <span>Your Progress</span>
            <span>{currentQuestion + 1}/{questions.length} Questions</span>
          </div>
          <div className="progress-bar">
            <div className="progress" style={{ width: `${progressPercentage}%` }}></div>
          </div>
        </div>

        <div className="question-container">
          <div className="question active">
            <h3>
              <i className={currentQuestionData.icon}></i>
              {currentQuestionData.title}
            </h3>

            {error && (
              <div className="error-message">
                <i className="fas fa-exclamation-triangle"></i>
                {error}
              </div>
            )}

            <div className="options">
              {currentQuestionData.options.map((option) => (
                <div
                  key={option.value}
                  className={`${currentQuestionData.type === 'checkbox' ? 'checkbox-option' : 'option'} ${
                    currentQuestionData.type === 'checkbox'
                      ? (formData[currentQuestionData.id] || []).includes(option.value) ? 'selected' : ''
                      : formData[currentQuestionData.id] === option.value ? 'selected' : ''
                  }`}
                  onClick={() => handleOptionChange(currentQuestionData.id, option.value, currentQuestionData.type === 'checkbox')}
                >
                  <input
                    type={currentQuestionData.type === 'checkbox' ? 'checkbox' : 'radio'}
                    id={`${currentQuestionData.id}-${option.value}`}
                    name={currentQuestionData.id}
                    value={option.value}
                    checked={
                      currentQuestionData.type === 'checkbox'
                        ? (formData[currentQuestionData.id] || []).includes(option.value)
                        : formData[currentQuestionData.id] === option.value
                    }
                    onChange={() => {}} // Handled by onClick
                  />
                  <label htmlFor={`${currentQuestionData.id}-${option.value}`}>
                    {option.label}
                  </label>
                </div>
              ))}
            </div>

            <div className="navigation">
              <button
                type="button"
                className="nav-button prev-button"
                onClick={prevQuestion}
                style={{ display: currentQuestion === 0 ? 'none' : 'block' }}
              >
                <i className="fas fa-arrow-left"></i> Previous
              </button>

              {currentQuestion < questions.length - 1 ? (
                <button
                  type="button"
                  className="nav-button next-button"
                  onClick={nextQuestion}
                >
                  Next <i className="fas fa-arrow-right"></i>
                </button>
              ) : (
                <button
                  type="button"
                  className="nav-button submit-button"
                  onClick={handleSubmit}
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <i className="fas fa-spinner fa-spin"></i> Submitting...
                    </>
                  ) : (
                    <>
                      Submit Assessment <i className="fas fa-check-circle"></i>
                    </>
                  )}
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default HealthAssessment;
