import React, { useState, useEffect, useRef } from 'react';
import './css/Dashboard.css';

function Dashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [username, setUsername] = useState('');
  const [userLocation, setUserLocation] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [locationSaved, setLocationSaved] = useState(false);
  const mapRef = useRef(null);
  const chartRef = useRef(null);

  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  useEffect(() => {
    initializeDashboard();
    
    // Set up real-time updates
    const interval = setInterval(() => {
      if (userLocation && username) {
        fetchDashboardData();
      }
    }, 10000); // Every 30 seconds

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (userLocation && username && !locationSaved) {
      saveUserLocation();
    }
  }, [userLocation, username, locationSaved]);

  useEffect(() => {
    if (userLocation && username && locationSaved) {
      fetchDashboardData();
    }
  }, [userLocation, username, locationSaved]);

  const initializeDashboard = () => {
    // Get username from localStorage
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const userName = user.name || 'User';
    setUsername(userName);

    if (!userName || userName === 'User') {
      setError('User not found. Please login again.');
      setLoading(false);
      return;
    }

    // Check if we have saved location in localStorage
    const savedLocation = localStorage.getItem('userLocation');
    if (savedLocation) {
      try {
        const location = JSON.parse(savedLocation);
        const now = new Date().getTime();
        const locationAge = now - (location.timestamp || 0);
        
        // Use saved location if it's less than 30 minutes old
        if (locationAge < 30 * 60 * 1000) {
          setUserLocation(location);
          setLocationSaved(true);
          console.log('Using saved location:', location);
          return;
        }
      } catch (error) {
        console.error('Error parsing saved location:', error);
      }
    }

    // Get fresh user location
    getUserLocation();
  };

  const getUserLocation = () => {
    if (!navigator.geolocation) {
      console.warn('Geolocation not supported, using default location');
      // Use default location (ASIET Campus) if geolocation fails
      const defaultLocation = {
        lat: 10.178385739668958,
        lng: 76.43052237497399,
        accuracy: null,
        timestamp: new Date().getTime(),
        source: 'default'
      };
      setUserLocation(defaultLocation);
      localStorage.setItem('userLocation', JSON.stringify(defaultLocation));
      return;
    }

    const options = {
      enableHighAccuracy: true,
      timeout: 15000,
      maximumAge: 600000 // 10 minutes
    };

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const location = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
          accuracy: position.coords.accuracy,
          timestamp: new Date().getTime(),
          source: 'gps'
        };
        setUserLocation(location);
        localStorage.setItem('userLocation', JSON.stringify(location));
        console.log('User location obtained:', location);
      },
      (error) => {
        console.error('Geolocation error:', error);
        // Use default location (ASIET Campus) if geolocation fails
        const defaultLocation = {
          lat: 10.178385739668958,
          lng: 76.43052237497399,
          accuracy: null,
          timestamp: new Date().getTime(),
          source: 'default_fallback'
        };
        setUserLocation(defaultLocation);
        localStorage.setItem('userLocation', JSON.stringify(defaultLocation));
      },
      options
    );
  };

  const saveUserLocation = async () => {
    if (!userLocation || !username) return;

    try {
      console.log('Saving user location to backend...');
      
      const response = await fetch(`${API_BASE_URL}/api/save-user-location/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username,
          latitude: userLocation.lat,
          longitude: userLocation.lng,
          accuracy: userLocation.accuracy,
          source: userLocation.source || 'gps'
        })
      });

      const data = await response.json();
      
      if (response.ok) {
        console.log('Location saved successfully:', data);
        setLocationSaved(true);
      } else {
        console.warn('Failed to save location:', data.message || data.error);
        // Still proceed with dashboard even if location save fails
        setLocationSaved(true);
      }
    } catch (error) {
      console.error('Error saving location:', error);
      // Still proceed with dashboard even if location save fails
      setLocationSaved(true);
    }
  };

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('username', username);
      
      // Send location data to backend for IDW calculation and nearest sensor detection
      if (userLocation) {
        url.searchParams.append('lat', userLocation.lat);
        url.searchParams.append('lng', userLocation.lng);
      }

      console.log('Fetching dashboard data from:', url.toString());

      const response = await fetch(url);
      const data = await response.json();
      console.log('Dashboard API response:', data);

      if (response.ok) {
        // Check if we need to redirect to health assessment
        if (data.redirect_to === '/health-assessment') {
          window.location.href = '/health-assessment';
          return;
        }
        
        setDashboardData(data);
        setError(null);
        
        // Initialize map and chart after data is loaded
        setTimeout(() => {
          initializeMap();
          initializeForecastChart();
        }, 500);
        
      } else {
        // Handle specific error cases
        if (response.status === 404 && data.redirect_to) {
          window.location.href = data.redirect_to;
          return;
        }
        
        setError(data.message || data.error || 'Failed to fetch dashboard data');
      }
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
      setError('Network error - please check your connection');
    } finally {
      setLoading(false);
    }
  };

  // Initialize Leaflet map using backend data
  const initializeMap = () => {
    if (typeof window !== 'undefined' && window.L && userLocation && dashboardData) {
      // Clear existing map if any
      if (mapRef.current) {
        mapRef.current.remove();
      }

      const map = window.L.map('dashboard-map').setView([userLocation.lat, userLocation.lng], 12);
      
      window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '¬© OpenStreetMap contributors'
      }).addTo(map);

      // Add user location marker
      const userIcon = window.L.divIcon({
        html: '<i class="fas fa-user-circle" style="color: #3b82f6; font-size: 20px;"></i>',
        iconSize: [20, 20],
        className: 'custom-div-icon'
      });
      
      const interpolatedAQI = dashboardData.interpolated_aqi || dashboardData.nearest_sensor_info?.current_aqi || 'N/A';
      
      window.L.marker([userLocation.lat, userLocation.lng], { icon: userIcon })
        .addTo(map)
        .bindPopup(`
          <strong>Your Location</strong><br/>
          ${userLocation.source === 'gps' ? 'üìç GPS Location' : 'üìç Default Location'}<br/>
          AQI: ${interpolatedAQI}<br/>
          ${userLocation.accuracy ? `Accuracy: ¬±${Math.round(userLocation.accuracy)}m` : ''}
        `)
        .openPopup();

      // Add station markers using backend data
      const allStationsData = dashboardData.all_stations_data || {};
      const sensorLocations = dashboardData.sensor_locations || {};
      
      Object.entries(sensorLocations).forEach(([stationId, location]) => {
        const stationData = allStationsData[stationId];
        const aqi = stationData?.highest_sub_index || 'N/A';
        
        const stationIcon = window.L.divIcon({
          html: `<div class="station-marker">
                  <i class="fas fa-broadcast-tower"></i>
                  <span>${aqi}</span>
                 </div>`,
          iconSize: [40, 40],
          className: 'custom-station-icon'
        });

        window.L.marker([location.lat, location.lng], { icon: stationIcon })
          .addTo(map)
          .bindPopup(`
            <strong>${location.displayName || location.name}</strong><br/>
            AQI: ${aqi}<br/>
            Status: ${getAQIStatus(aqi).status}<br/>
            Latest Update: ${stationData?.latest_item?.received_at || 'N/A'}
          `);
      });

      mapRef.current = map;
    }
  };

  // Initialize forecast chart using backend forecast data
  const initializeForecastChart = () => {
    if (typeof window !== 'undefined' && window.Chart && dashboardData) {
      const ctx = document.getElementById('forecast-chart');
      if (!ctx) return;

      if (chartRef.current) {
        chartRef.current.destroy();
      }

      // Use forecast data from backend
      const forecastData = dashboardData.forecast_data?.nearest_station || [];
      
      if (forecastData.length === 0) {
        // Use mock data if no forecast available
        const mockData = generateMockForecast();
        const data = mockData.map(item => item[`${selectedParameter}_max`]);
        const labels = mockData.map(item => item.day);
        
        chartRef.current = new window.Chart(ctx, {
          type: 'line',
          data: {
            labels: labels,
            datasets: [{
              label: selectedParameter.toUpperCase() + ' (Sample)',
              data: data,
              borderColor: '#f59e0b',
              backgroundColor: 'rgba(245, 158, 11, 0.1)',
              tension: 0.4,
              fill: true
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
              y: {
                beginAtZero: true
              }
            }
          }
        });
        return;
      }

      const data = forecastData.map(item => item[`${selectedParameter}_max`] || 0);
      const labels = forecastData.map(item => item.day);

      chartRef.current = new window.Chart(ctx, {
        type: 'line',
        data: {
          labels: labels,
          datasets: [{
            label: selectedParameter.toUpperCase(),
            data: data,
            borderColor: '#3b82f6',
            backgroundColor: 'rgba(59, 130, 246, 0.1)',
            tension: 0.4,
            fill: true
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            y: {
              beginAtZero: true
            }
          }
        }
      });
    }
  };

  const generateMockForecast = () => {
    const days = ['Today', 'Tomorrow', 'Day 3', 'Day 4', 'Day 5'];
    return days.map((day, index) => ({
      day,
      pm25_max: Math.round(25 + Math.random() * 30),
      pm10_max: Math.round(40 + Math.random() * 40),
      so2_max: Math.round(20 + Math.random() * 30),
      no2_max: Math.round(30 + Math.random() * 25),
      co_max: (1.0 + Math.random() * 2.0).toFixed(1),
      o3_max: Math.round(40 + Math.random() * 35),
      nh3_max: Math.round(10 + Math.random() * 20)
    }));
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const getAQIStatus = (aqiValue) => {
    if (!aqiValue || aqiValue === 'N/A') return { status: 'Unknown', color: '#6b7280', alertClass: 'good' };
    
    const value = parseInt(aqiValue, 10);
    if (value <= 50) {
      return { status: 'GOOD', color: '#10b981', alertClass: 'good' };
    } else if (value <= 100) {
      return { status: 'MODERATE', color: '#f59e0b', alertClass: 'moderate' };
    } else if (value <= 150) {
      return { status: 'UNHEALTHY', color: '#ef4444', alertClass: 'unhealthy' };
    } else {
      return { status: 'HAZARDOUS', color: '#7c2d12', alertClass: 'hazardous' };
    }
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-IN', {
        timeZone: 'Asia/Kolkata',
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return dateString;
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('userLocation');
    window.location.href = '/login';
  };

  const handleRetry = () => {
    setError(null);
    setLocationSaved(false);
    localStorage.removeItem('userLocation');
    initializeDashboard();
  };

  const handleParameterChange = (parameter) => {
    setSelectedParameter(parameter);
    
    // Update active button
    document.querySelectorAll('.param-btn').forEach(btn => {
      btn.classList.remove('active');
    });
    const targetBtn = document.querySelector(`[data-param="${parameter}"]`);
    if (targetBtn) {
      targetBtn.classList.add('active');
    }
    
    // Reinitialize chart with new parameter
    if (chartRef.current) {
      chartRef.current.destroy();
      chartRef.current = null;
    }
    setTimeout(initializeForecastChart, 100);
  };

  const refreshLocation = () => {
    setLocationSaved(false);
    localStorage.removeItem('userLocation');
    getUserLocation();
  };

  if (loading) {
    return (
      <div className="dashboard-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading dashboard data...</p>
          <small>
            {!userLocation ? 'Getting your location...' : 
             !locationSaved ? 'Saving location data...' : 
             'Fetching air quality data...'}
          </small>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard-page">
        <div className="error-container">
          <div className="error-message">
            <h2>‚ö†Ô∏è Dashboard Unavailable</h2>
            <p>{error}</p>
            <button onClick={handleRetry} className="retry-button">
              Retry
            </button>
            {error.includes('login') && (
              <button onClick={handleLogout} className="retry-button" style={{ marginLeft: '10px' }}>
                Go to Login
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  // Extract data from backend response
  const currentData = dashboardData?.nearest_sensor_info || {};
  const healthData = dashboardData?.health_data || {};
  const interpolatedValues = dashboardData?.idw_interpolated_values || {};
  const interpolatedAQI = dashboardData?.interpolated_aqi || currentData.current_aqi;
  const nearestStation = {
    name: currentData.current_station_name,
    distance: currentData.distance_to_nearest
  };

  // Use IDW interpolated values if available, otherwise use nearest station data
  const displayValues = {
    pm25: interpolatedValues.pm25?.toFixed(1) || currentData.current_sensor_data?.pm25 || '25',
    pm10: interpolatedValues.pm10?.toFixed(1) || currentData.current_sensor_data?.pm10 || '40',
    so2: interpolatedValues.so2?.toFixed(1) || currentData.current_sensor_data?.so2 || '35',
    no2: interpolatedValues.no2?.toFixed(1) || currentData.current_sensor_data?.no2 || '37',
    co: interpolatedValues.co?.toFixed(1) || currentData.current_sensor_data?.co || '1.2',
    o3: interpolatedValues.o3?.toFixed(1) || currentData.current_sensor_data?.o3 || '45',
    nh3: interpolatedValues.nh3?.toFixed(1) || currentData.current_sensor_data?.nh3 || '15'
  };

  const aqiStatus = getAQIStatus(interpolatedAQI);
  const isIDWActive = dashboardData?.idw_status === 'active';

  return (
    <div className="dashboard-page">
      {/* Real-time Status Indicator */}
      <div className="realtime-status">
        üî¥ LIVE ‚Ä¢ Updates every 30s ‚Ä¢ {userLocation?.source === 'gps' ? 
          `GPS ¬±${Math.round(userLocation.accuracy || 0)}m` : 'Default Location'} ‚Ä¢ 
        {isIDWActive ? 'IDW Active' : 'Nearest Station'}
      </div>

      {/* Navigation */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" />
            AirAware
          </a>

          <div className="menu-toggle" onClick={toggleMenu}>
            <i className="fas fa-bars"></i>
          </div>

          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><a href="/" className="nav-link">
              <i className="fas fa-home"></i> Home
            </a></li>
            <li><a href="/forecast" className="nav-link">
              <i className="fas fa-chart-line"></i> Forecasting
            </a></li>
            <li><a href="/health-assessment" className="nav-link">
              <i className="fas fa-notes-medical"></i> Health Update
            </a></li>
            <li><a href="/health-report" className="nav-link">
              <i className="fas fa-file-medical-alt"></i> Health Report
            </a></li>
            <li><a href="/add-family" className="nav-link">
              <i className="fas fa-user-plus"></i> Add Family
            </a></li>

            <li className="user-info">
              <i className="fas fa-user-circle"></i>
              <span>{username}</span>
            </li>
            <li>
              <button onClick={handleLogout} className="nav-link login-btn">
                <i className="fas fa-sign-out-alt"></i> Logout
              </button>
            </li>
          </ul>
        </div>
      </nav>

      {/* Alert Banner */}
      <div className={`alert-banner ${aqiStatus.alertClass}`}>
        <i className="fas fa-info-circle alert-icon"></i>
        <span>
          Your Location AQI: {Math.round(interpolatedAQI) || 'N/A'} - {aqiStatus.status}
          {nearestStation.name && nearestStation.distance && 
            ` ‚Ä¢ Nearest: ${nearestStation.name} (${nearestStation.distance.toFixed(1)}km)`}
        </span>
      </div>

      {/* Main Container */}
      <div className="main-container">
        {/* Welcome Header */}
        <div className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">
                <i className="fas fa-user"></i>
              </div>
              <div className="welcome-text">
                <h1>Welcome, {username}!</h1>
                <div className="location-info">
                  <i className="fas fa-map-marker-alt"></i>
                  <span>
                    üìç {nearestStation.name ? `Near ${nearestStation.name}` : 'Locating...'}
                    {userLocation?.source === 'gps' && userLocation?.accuracy && 
                      ` (¬±${Math.round(userLocation.accuracy)}m)`}
                  </span>
                  <button 
                    onClick={refreshLocation}
                    className="refresh-location-btn"
                    title="Refresh location"
                  >
                    <i className="fas fa-sync-alt"></i>
                  </button>
                </div>
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>
                {Math.round(interpolatedAQI) || 'N/A'}
              </div>
              <div className="aqi-status">
                {isIDWActive ? 'IDW Interpolated AQI' : 'Nearest Station AQI'}
              </div>
              <div className="aqi-badge">
                Your Location
              </div>
              <div className="last-updated">
                Last updated: {formatDateTime(dashboardData?.timestamp)}
              </div>
            </div>
          </div>
        </div>

        {/* Location & IDW Status */}
        <div className="sensor-notification">
          <i className="fas fa-satellite-dish notification-icon"></i>
          <strong>
            {isIDWActive ? 
              'üßÆ IDW Interpolation Active' : 
              'üì° Using Nearest Station Data'
            }
          </strong>
          {isIDWActive ? 
            ' - Values calculated from multiple monitoring stations using Inverse Distance Weighting for your exact location.' :
            ' - Showing data from your nearest monitoring station.'
          }
          <br />
          Current source: <strong>{nearestStation?.name || 'Unknown Station'}</strong>
          {nearestStation?.distance && ` - ${nearestStation.distance.toFixed(1)}km away`}
          {userLocation?.source === 'default' && 
            <span style={{ color: '#f59e0b' }}> (Using default location - enable GPS for precise data)</span>
          }
        </div>

        {/* Air Quality Metrics */}
        <div className="metrics-section">
          <h2 className="section-title">
            üå¨Ô∏è Current Air Quality at Your Location 
            {isIDWActive && <small>(IDW Calculated)</small>}
          </h2>
          <div className="metrics-grid">
            <MetricCard
              icon="fas fa-flask"
              value={displayValues.nh3}
              label="NH‚ÇÉ (Ammonia)"
              unit="¬µg/m¬≥"
              interpolated={isIDWActive}
            />
            <MetricCard
              icon="fas fa-sun"
              value={displayValues.o3}
              label="O‚ÇÉ (Ozone)"
              unit="¬µg/m¬≥"
              interpolated={isIDWActive}
            />
            <MetricCard
              icon="fas fa-industry"
              value={displayValues.so2}
              label="SO‚ÇÇ (Sulfur Dioxide)"
              unit="¬µg/m¬≥"
              interpolated={isIDWActive}
            />
            <MetricCard
              icon="fas fa-cloud"
              value={displayValues.co}
              label="CO (Carbon Monoxide)"
              unit="mg/m¬≥"
              interpolated={isIDWActive}
            />
            <MetricCard
              icon="fas fa-smog"
              value={displayValues.no2}
              label="NO‚ÇÇ (Nitrogen Dioxide)"
              unit="¬µg/m¬≥"
              interpolated={isIDWActive}
            />
            <MetricCard
              icon="fas fa-seedling"
              value={displayValues.pm25}
              label="PM2.5"
              unit="¬µg/m¬≥"
              interpolated={isIDWActive}
            />
            <MetricCard
              icon="fas fa-leaf"
              value={displayValues.pm10}
              label="PM10"
              unit="¬µg/m¬≥"
              interpolated={isIDWActive}
            />
          </div>
        </div>

        {/* Dashboard Grid */}
        <div className="dashboard-grid">
          {/* Interactive Map */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">üó∫Ô∏è Interactive Sensor Map</h3>
            </div>
            <div className="map-container">
              <div id="dashboard-map" style={{ height: '300px', width: '100%' }}></div>
            </div>
            <div style={{ marginTop: '12px', textAlign: 'center', fontSize: '0.875rem', color: '#6b7280' }}>
              <i className="fas fa-info-circle"></i>
              Blue icon: Your location ‚Ä¢ Tower icons: Monitoring stations
              {userLocation?.source !== 'gps' && 
                <span style={{ color: '#f59e0b' }}> ‚Ä¢ Enable location for GPS accuracy</span>
              }
            </div>
          </div>

          {/* AQI Forecast */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3 className="card-title">üìä 5-Day Air Quality Forecast</h3>
            </div>
            <div className="forecast-content">
              <div className="forecast-controls">
                {['pm25', 'pm10', 'no2', 'o3', 'so2', 'co', 'nh3'].map(param => (
                  <button 
                    key={param}
                    className={`param-btn ${selectedParameter === param ? 'active' : ''}`}
                    data-param={param}
                    onClick={() => handleParameterChange(param)}
                  >
                    {param.toUpperCase()}
                  </button>
                ))}
              </div>
              <div className="chart-container" style={{ height: '200px', marginTop: '15px' }}>
                <canvas id="forecast-chart"></canvas>
              </div>
              <div className="forecast-table-container" style={{ marginTop: '15px' }}>
                <table className="forecast-table">
                  <thead>
                    <tr>
                      <th>üìÖ Day</th>
                      <th>üìà Max Value</th>
                    </tr>
                  </thead>
                  <tbody>
                    {(dashboardData?.forecast_data?.nearest_station || generateMockForecast()).map((item, index) => (
                      <tr key={index}>
                        <td>{item.day}</td>
                        <td>{item[`${selectedParameter}_max`] || 'N/A'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        {/* Health Status Section */}
        {healthData && healthData.score && (
          <div className="health-section">
            <div className="health-content">
              <div className="health-chart-container">
                <div className="card-header">
                  <h3 className="card-title">‚ù§Ô∏è Your Personalized Health Status</h3>
                </div>
                <div className="health-score-display">
                  <div className="health-score-circle" style={{ borderColor: healthData.risk_color }}>
                    <div className="health-score-value" style={{ color: healthData.risk_color }}>
                      {healthData.score}
                    </div>
                    <div className="health-risk-level" style={{ color: healthData.risk_color }}>
                      {healthData.risk_level}
                    </div>
                  </div>
                  <div className="health-score-label">Health Score</div>
                </div>
              </div>
              <div className="health-alert">
                <h3>
                  <i className="fas fa-shield-alt"></i>
                  Location-Based Health Advisory
                </h3>
                <p>
                  Based on your health profile and current air quality at your location (AQI: {Math.round(interpolatedAQI)}), 
                  your risk level is <strong style={{ color: healthData.risk_color }}>
                  {healthData.risk_level}</strong>.
                  {healthData.is_high_risk 
                    ? ' We recommend avoiding outdoor activities and following safety guidelines.' 
                    : ' You can enjoy normal outdoor activities with standard precautions.'
                  }
                </p>
                
                {healthData.recommendations && healthData.recommendations.length > 0 && (
                  <div className="health-recommendations">
                    <h4>üéØ Today's Recommendations:</h4>
                    <ul>
                      {healthData.recommendations.slice(0, 3).map((rec, index) => (
                        <li key={index}>{rec}</li>
                      ))}
                    </ul>
                  </div>
                )}
                
                {healthData.priority_actions && healthData.priority_actions.length > 0 && (
                  <div className="priority-actions">
                    <h4>‚ö†Ô∏è Immediate Actions:</h4>
                    <ul>
                      {healthData.priority_actions.slice(0, 2).map((action, index) => (
                        <li key={index} style={{ color: '#ef4444' }}>{action}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {/* Weather Information from Nearest Station */}
        <div className="weather-section">
          <h2 className="section-title">üå§Ô∏è Weather Conditions (Nearest Station)</h2>
          <div className="weather-grid">
            <WeatherCard
              icon="fas fa-temperature-high"
              value={`${currentData.current_sensor_data?.temp || '28'}¬∞C`}
              label="Temperature"
              source={nearestStation?.name}
            />
            <WeatherCard
              icon="fas fa-tint"
              value={`${currentData.current_sensor_data?.hum || '65'}%`}
              label="Humidity"
              source={nearestStation?.name}
            />
            <WeatherCard
              icon="fas fa-compress-alt"
              value={`${currentData.current_sensor_data?.pre || '1013'} hPa`}
              label="Atmospheric Pressure"
              source={nearestStation?.name}
            />
          </div>
        </div>

        {/* Data Disclaimer */}
        <div className="disclaimer">
          <h3>
            <i className="fas fa-info-circle"></i>
            üìã Advanced {isIDWActive ? 'IDW Interpolation' : 'Nearest Station'} & Data Methodology
          </h3>
          <p>
            <strong>Location-Specific Air Quality:</strong> {isIDWActive ? 
              'This system uses Inverse Distance Weighting (IDW) interpolation to calculate air quality values specific to your exact location using data from multiple monitoring stations.' :
              'This system shows data from your nearest monitoring station for the most relevant air quality information.'
            }
          </p>

          <div className="disclaimer-highlight">
            <p><strong>{isIDWActive ? 'üßÆ IDW Interpolation Method:' : 'üì° Nearest Station Method:'}</strong></p>
            <ul>
              {isIDWActive ? (
                <>
                  <li><strong>Multi-station data fusion:</strong> Combines readings from all available monitoring stations</li>
                  <li><strong>Distance-weighted calculation:</strong> Closer stations have more influence on your readings</li>
                  <li><strong>Real-time accuracy:</strong> Updates every 30 seconds with latest sensor data</li>
                  <li><strong>GPS integration:</strong> Uses your precise location for accurate interpolation</li>
                </>
              ) : (
                <>
                  <li><strong>Nearest station selection:</strong> Automatically finds closest monitoring station</li>
                  <li><strong>Distance calculation:</strong> Shows exact distance to selected station</li>
                  <li><strong>Real-time updates:</strong> Updates every 30 seconds with latest sensor data</li>
                  <li><strong>Location awareness:</strong> Updates when you move to different areas</li>
                </>
              )}
              <li><strong>Government compliance:</strong> All stations follow CPCB standards and protocols</li>
            </ul>
          </div>

          <p>
            <strong>‚ö†Ô∏è Location & Usage:</strong> Air quality values are {isIDWActive ? 
              'calculated specifically for your GPS location. Move to different areas to see updated values.' :
              'from your nearest monitoring station. Enable GPS for more precise location-based calculations.'
            } For medical concerns, consult healthcare professionals.
          </p>

          <p>
            <strong>üîÑ System Status:</strong> 
            <span style={{ color: '#10b981', fontWeight: 600 }}> OPERATIONAL</span> | 
            {isIDWActive ? 'IDW Active' : 'Nearest Station Mode'} | 
            GPS: {userLocation?.source === 'gps' ? 
              `Active (¬±${Math.round(userLocation.accuracy || 0)}m)` : 
              'Default Location'
            } | 
            Last update: {formatDateTime(dashboardData?.timestamp)}
          </p>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-grid">
            <div className="footer-section">
              <h3>About AirQuality Kerala</h3>
              <p className="footer-text">
                Advanced air quality monitoring with {isIDWActive ? 'IDW interpolation' : 'nearest station'} technology, 
                providing location-specific readings for better health decisions across Kerala.
              </p>
              <div className="social-links">
                <a href="#" className="social-link"><i className="fab fa-facebook-f"></i></a>
                <a href="#" className="social-link"><i className="fab fa-twitter"></i></a>
                <a href="#" className="social-link"><i className="fab fa-linkedin-in"></i></a>
                <a href="#" className="social-link"><i className="fab fa-instagram"></i></a>
              </div>
            </div>

            <div className="footer-section">
              <h3>Quick Links</h3>
              <a href="/" className="footer-link">Home</a>
              <a href="/map" className="footer-link">Live Map</a>
              <a href="/forecast" className="footer-link">Forecasting</a>
              <a href="/health-report" className="footer-link">Health Report</a>
            </div>

            <div className="footer-section">
              <h3>Services</h3>
              <a href="/forecast" className="footer-link">AQI Forecasting</a>
              <a href="/health-assessment" className="footer-link">Health Assessment</a>
              <a href="/map" className="footer-link">Live AQI Map</a>
              <a href="#" className="footer-link">{isIDWActive ? 'IDW Analytics' : 'Station Analytics'}</a>
            </div>

            <div className="footer-section">
              <h3>Contact Information</h3>
              <p className="footer-text">
                Vidya Bharathi Nagar, Mattoor<br />
                Kalady 683574, Ernakulam<br />
                Kerala, India
              </p>
              <br />
              <p className="footer-text">
                <strong>Email:</strong> aiiot@adishankara.ac.in<br />
                <strong>Phone:</strong> 0484 246 3825
              </p>
            </div>
          </div>

          <div className="footer-bottom">
            <p>&copy; 2025 AirQuality Kerala. All rights reserved. Developed by ASIET AI & IoT Lab.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

// Helper Components
function MetricCard({ icon, value, label, unit, interpolated }) {
  return (
    <div className="metric-card">
      <div className="metric-icon">
        <i className={icon}></i>
      </div>
      <div className="metric-value">{value}</div>
      <div className="metric-label">{label}</div>
      {unit && <div className="metric-unit">{unit}</div>}
      {interpolated && (
        <div className="metric-badge">
          <i className="fas fa-calculator"></i> IDW
        </div>
      )}
    </div>
  );
}

function WeatherCard({ icon, value, label, source }) {
  return (
    <div className="weather-card">
      <div className="weather-icon">
        <i className={icon}></i>
      </div>
      <div className="weather-value">{value}</div>
      <div className="weather-label">{label}</div>
      {source && (
        <div className="weather-source">
          <small>From: {source}</small>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
