import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import './css/Dashboard.css';

// Lazy loading components
const LazyMap = React.lazy(() => 
  import('./LazyMap').catch(() => ({ 
    default: () => (
      <div className="map-fallback">
        <div className="map-placeholder">
          <div className="map-icon">ðŸ—ºï¸</div>
          <p>Interactive map loading...</p>
        </div>
      </div>
    )
  }))
);

const LazyChart = React.lazy(() => 
  import('./LazyChart').catch(() => ({ 
    default: () => (
      <div className="chart-fallback">
        <div className="chart-placeholder">
          <div className="chart-icon">ðŸ“Š</div>
          <p>Chart loading...</p>
        </div>
      </div>
    )
  }))
);

// Utility Functions
const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 6371; // km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + 
           Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
           Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

// Reverse geocoding to get location name
const getLocationName = async (lat, lng) => {
  try {
    const response = await fetch(
      `https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${lat}&longitude=${lng}&localityLanguage=en`
    );
    const data = await response.json();
    
    if (data && data.locality) {
      return {
        display_name: `${data.locality}, ${data.principalSubdivision || data.countryName}`,
        city: data.locality,
        state: data.principalSubdivision,
        country: data.countryName
      };
    }
    
    return {
      display_name: `${lat.toFixed(4)}, ${lng.toFixed(4)}`,
      city: 'Unknown Location',
      state: 'Unknown State',
      country: 'Unknown Country'
    };
  } catch (error) {
    console.log('âŒ Reverse geocoding failed:', error);
    return {
      display_name: `${lat.toFixed(4)}, ${lng.toFixed(4)}`,
      city: 'Unknown Location',
      state: 'Unknown State', 
      country: 'Unknown Country'
    };
  }
};

function Dashboard() {
  // ===== STATE MANAGEMENT =====
  const [username] = useState(() => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      return user.name || 'User';
    } catch {
      return 'User';
    }
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStationInfo, setNearestStationInfo] = useState(null);
  const [currentDataInfo, setCurrentDataInfo] = useState(null);
  const [locationStatus, setLocationStatus] = useState('initializing');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isLocationUpdating, setIsLocationUpdating] = useState(false);
  const [lastUpdateTime, setLastUpdateTime] = useState(new Date());

  const navigate = useNavigate();
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';
  const locationTimeoutRef = useRef(null);
  const abortControllerRef = useRef(null);

  // ===== GET USER LOCATION =====
  const getUserLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation not supported'));
        return;
      }

      setLocationStatus('detecting');
      setIsLocationUpdating(true);

      const timeout = setTimeout(() => {
        reject(new Error('Location detection timeout'));
      }, 10000);

      navigator.geolocation.getCurrentPosition(
        async (position) => {
          clearTimeout(timeout);
          
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            accuracy: position.coords.accuracy,
            source: 'gps',
            timestamp: Date.now()
          };
          
          if (isNaN(location.lat) || isNaN(location.lng)) {
            reject(new Error('Invalid GPS coordinates'));
            return;
          }
          
          // Get location name
          try {
            const locationName = await getLocationName(location.lat, location.lng);
            setUserLocationName(locationName);
            console.log('ðŸ“ Location name resolved:', locationName.display_name);
          } catch (nameError) {
            console.log('âŒ Failed to get location name:', nameError);
            setUserLocationName({
              display_name: `${location.lat.toFixed(4)}, ${location.lng.toFixed(4)}`,
