// Enhanced LazyMap.js - Works with backend data structure

import React, { useEffect, useRef } from 'react';

const LazyMap = ({ userLocation, dashboardData, currentAQI, dataType }) => {
  const mapRef = useRef(null);
  const containerRef = useRef(null);

  useEffect(() => {
    // Only initialize if Leaflet is available
    if (!window.L) {
      console.log('‚ùå Leaflet not available');
      return;
    }

    // Don't initialize map without location
    if (!userLocation || !userLocation.lat || !userLocation.lng) {
      console.log('‚ùå User location not available');
      return;
    }

    const initMap = () => {
      try {
        // Clean up existing map
        if (mapRef.current) {
          mapRef.current.remove();
          mapRef.current = null;
        }

        if (!containerRef.current) {
          console.log('‚ùå Container ref not available');
          return;
        }

        console.log('üó∫Ô∏è Initializing map with location:', userLocation);

        // Create map centered on user location
        const map = window.L.map(containerRef.current, {
          center: [userLocation.lat, userLocation.lng],
          zoom: 13,
          zoomControl: true,
          attributionControl: true,
          scrollWheelZoom: true,
          dragging: true,
          touchZoom: true,
          doubleClickZoom: true,
          boxZoom: true,
          keyboard: true
        });

        // Add tile layer
        window.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          maxZoom: 18,
          attribution: '¬© OpenStreetMap contributors'
        }).addTo(map);

        // Add user marker with smart styling
        const userMarkerHtml = dataType === 'interpolated' 
          ? `<div style="
              background: linear-gradient(135deg, #10b981 0%, #059669 100%);
              color: white;
              border-radius: 50%;
              width: 28px;
              height: 28px;
              display: flex;
              align-items: center;
              justify-content: center;
              border: 3px solid white;
              box-shadow: 0 4px 12px rgba(16, 185, 129, 0.4);
              font-size: 14px;
              font-weight: bold;
              animation: pulse 2s infinite;
            ">üéØ</div>`
          : `<div style="
              background: linear-gradient(135deg, #3b82f6 0%, #2563eb 100%);
              color: white;
              border-radius: 50%;
              width: 28px;
              height: 28px;
              display: flex;
              align-items: center;
              justify-content: center;
              border: 3px solid white;
              box-shadow: 0 4px 12px rgba(59, 130, 246, 0.4);
              font-size: 14px;
              font-weight: bold;
            ">üìç</div>`;

        const userIcon = window.L.divIcon({
          html: userMarkerHtml,
          iconSize: [34, 34],
          iconAnchor: [17, 17],
          popupAnchor: [0, -17],
          className: 'custom-user-marker'
        });

        const userMarker = window.L.marker([userLocation.lat, userLocation.lng], { icon: userIcon })
          .addTo(map);

        // User popup content
        const userPopupContent = `
          <div style="text-align: center; padding: 8px; min-width: 200px;">
            <h4 style="margin: 0 0 8px 0; color: #1f2937; font-size: 14px;">
              ${dataType === 'interpolated' ? 'üéØ Your Location (Smart Analysis)' : 'üìç Your Location'}
            </h4>
            <div style="color: #4b5563; font-size: 12px; margin-bottom: 8px;">
              AQI: <strong style="color: ${currentAQI <= 50 ? '#10b981' : currentAQI <= 100 ? '#f59e0b' : '#ef4444'};">${Math.round(currentAQI)}</strong>
            </div>
            <div style="color: #6b7280; font-size: 11px;">
              ${dataType === 'interpolated' 
                ? 'Showing personalized air quality values' 
                : 'Showing nearest station data'
              }
            </div>
          </div>
        `;

        userMarker.bindPopup(userPopupContent).openPopup();

        // Add station markers from backend data
        let stationLocations = {};
        
        // Get station locations from different possible sources
        if (dashboardData?.sensor_locations) {
          stationLocations = dashboardData.sensor_locations;
        } else if (dashboardData?.all_stations_data) {
          // Extract locations from all_stations_data
          Object.entries(dashboardData.all_stations_data).forEach(([stationId, stationData]) => {
            if (stationData.location) {
              stationLocations[stationId] = stationData.location;
            }
          });
        }

        console.log('üó∫Ô∏è Station locations:', stationLocations);

        // Add station markers
        Object.entries(stationLocations).forEach(([stationId, location]) => {
          if (!location.lat || !location.lng) {
            console.log(`‚ùå Invalid location for station ${stationId}`);
            return;
          }

          // Get AQI for this station
          let stationAQI = 50;
          let stationName = location.name || location.displayName || `Station ${stationId}`;
          
          if (dashboardData?.all_stations_data?.[stationId]) {
            stationAQI = dashboardData.all_stations_data[stationId].highest_sub_index || 50;
            stationName = dashboardData.all_stations_data[stationId].station_name || stationName;
          }

          // Station marker styling based on AQI
          const getStationColor = (aqi) => {
            if (aqi <= 50) return '#10b981';
            if (aqi <= 100) return '#f59e0b';
            if (aqi <= 150) return '#ef4444';
            return '#7c2d12';
          };

          const stationColor = getStationColor(stationAQI);

          const stationMarkerHtml = `
            <div style="
              background: ${stationColor};
              color: white;
              border-radius: 8px;
              padding: 4px 8px;
              font-size: 11px;
              font-weight: bold;
              border: 2px solid white;
              box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
              display: flex;
              flex-direction: column;
              align-items: center;
              min-width: 50px;
              text-align: center;
              line-height: 1.2;
            ">
              <div style="font-size: 12px;">üì°</div>
              <div style="font-size: 10px;">${Math.round(stationAQI)}</div>
            </div>
          `;

          const stationIcon = window.L.divIcon({
            html: stationMarkerHtml,
            iconSize: [60, 40],
            iconAnchor: [30, 40],
            popupAnchor: [0, -40],
            className: 'custom-station-marker'
          });

          const stationMarker = window.L.marker([location.lat, location.lng], { icon: stationIcon })
            .addTo(map);

          // Station popup content
          const stationPopupContent = `
            <div style="text-align: center; padding: 8px; min-width: 180px;">
              <h4 style="margin: 0 0 8px 0; color: #1f2937; font-size: 14px;">
                üì° ${stationName}
              </h4>
              <div style="color: #4b5563; font-size: 12px; margin-bottom: 8px;">
                AQI: <strong style="color: ${stationColor};">${Math.round(stationAQI)}</strong>
              </div>
              <div style="color: #6b7280; font-size: 11px;">
                Monitoring Station
              </div>
            </div>
          `;

          stationMarker.bindPopup(stationPopupContent);
        });

        // Add a circle around user location if interpolated
        if (dataType === 'interpolated') {
          window.L.circle([userLocation.lat, userLocation.lng], {
            color: '#10b981',
            fillColor: '#10b981',
            fillOpacity: 0.1,
            radius: 1000, // 1km radius
            weight: 2,
            dashArray: '5, 5'
          }).addTo(map).bindPopup('1km Smart Analysis Zone');
        }

        mapRef.current = map;
        console.log('‚úÖ Map initialized successfully');

      } catch (error) {
        console.error('‚ùå Map initialization failed:', error);
      }
    };

    // Initialize map with a small delay to ensure DOM is ready
    setTimeout(initMap, 100);

    return () => {
      if (mapRef.current) {
        try {
          mapRef.current.remove();
        } catch (e) {
          console.log('Error removing map:', e);
        }
        mapRef.current = null;
      }
    };
  }, [userLocation, dashboardData, currentAQI, dataType]);

  return (
    <div 
      ref={containerRef}
      style={{ 
        height: '300px', 
        width: '100%',
        borderRadius: '12px',
        background: '#f8fafc',
        border: '1px solid #e2e8f0',
        overflow: 'hidden'
      }}
    />
  );
};

export default LazyMap;
