import React, { useState, useEffect, useCallback, useMemo, Suspense } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/Dashboard.css';

const LazyMap = React.lazy(() => import('./LazyMap'));
const LazyChart = React.lazy(() => import('./LazyChart'));

const getIPLocation = async () => { /* ... IP location function ... */ };
const calculateDistance = (lat1, lon1, lat2, lon2) => { /* ... distance calculation logic ... */ };
const getAQIColor = (aqi) => { /* ... color logic ... */ };
const getAQIStatus = (aqi) => { /* ... status logic ... */ };

function Dashboard() {
    const { user, logout } = useAuth();
    const [username] = useState(user?.name || 'User');
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [dashboardData, setDashboardData] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [userLocationName, setUserLocationName] = useState('Detecting location...');
    const [nearestStation, setNearestStation] = useState(null);
    const [interpolatedData, setInterpolatedData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    // ... (All your data fetching and calculation useEffects from the previous step)

    const handleLogout = useCallback(() => {
        logout();
        navigate('/');
    }, [logout, navigate]);
    
    if (loading) return <div className="panel-loader"><h2>Loading Dashboard...</h2></div>;
    if (error) return <div className="error-message"><h2>Error</h2><p>{error}</p></div>;
    if (!dashboardData) return <div>No data available.</div>;

    const { health_data, stations, forecasts } = dashboardData;
    const displayAqi = interpolatedData?.aqi || Object.values(stations || {})[0]?.highest_sub_index || 0;
    const alertInfo = useMemo(() => {
        if (displayAqi <= 50) return { className: 'good', message: 'Air quality is good. Enjoy your day!' };
        if (displayAqi <= 100) return { className: 'moderate', message: 'Air quality is moderate. Sensitive individuals should take precautions.' };
        return { className: 'unhealthy', message: 'Air quality is unhealthy. It is advisable to reduce outdoor activities.' };
    }, [displayAqi]);
    
    return (
        <div className="dashboard-page">
            <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                        <img src="/aqi.webp" alt="AQM Logo" width="40" height="40" />
                        AirAware
                    </Link>
                    <div className="menu-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>
                        <i className={`fas ${isMenuOpen ? 'fa-times' : 'fa-bars'}`}></i>
                    </div>
                    <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
                        <li><Link to="/" className="nav-link">Home</Link></li>
                        <li><Link to="/map" className="nav-link">Live Map</Link></li>
                        {user && (
                            <>
                                <li><Link to="/health-report" className="nav-link">Health Report</Link></li>
                                <li><Link to="/add-family" className="nav-link">Add Family</Link></li>
                            </>
                        )}
                    </ul>
                    <div className="user-menu">
                        {user ? (
                            <>
                                <span className="user-name">Hello, {user.name}</span>
                                <button onClick={handleLogout} className="nav-link logout-btn">Logout</button>
                            </>
                        ) : (
                            <Link to="/login" className="nav-link login-btn">Login</Link>
                        )}
                    </div>
                </div>
            </nav>
            <div className="main-container">
                <div className={`alert-banner ${alertInfo.className}`}>
                    <i className="fas fa-info-circle"></i>
                    <span>{alertInfo.message}</span>
                </div>
                <div className="welcome-header">
                    <div className="welcome-text">
                        <h1>Welcome, {username}!</h1>
                        <p>{userLocationName}</p>
                    </div>
                    <div className="aqi-overview">
                        <div className="aqi-value" style={{ color: getAQIColor(displayAqi) }}>{Math.round(displayAqi)}</div>
                        <div className="aqi-status">{getAQIStatus(displayAqi)}</div>
                    </div>
                </div>

                {nearestStation && (
                    <div className="proximity-indicator">
                         <p>{`You are ${nearestStation.distance.toFixed(1)}km from ${nearestStation.name}.`}</p>
                    </div>
                )}
                
                <div className="dashboard-grid">
                    {/* Map and Forecast Cards */}
                </div>
                
                <div className="metrics-section">
                    <h2 className="section-title">Current Pollutant Levels</h2>
                    <div className="metrics-grid">{/* Pollutant Cards */}</div>
                </div>

                <div className="health-section">
                    <div className="health-content">
                        <h3>Your Health Status</h3>
                        <p>Risk Level: <span style={{color: health_data.risk_level === 'Low' ? '#10b981' : '#ef4444'}}>{health_data.risk_level}</span></p>
                    </div>
                    <div className="health-advisory">
                        <h3>General Health Advisory</h3>
                        <p>Follow health advisories closely.</p>
                    </div>
                </div>
            </div>
            <footer className="footer">
                <div className="footer-container">
                    <div className="footer-grid">
                        <div className="footer-section">
                            <h3>About AirAware</h3>
                            <p className="footer-text">Real-time air quality monitoring for a healthier Kerala.</p>
                        </div>
                        <div className="footer-section">
                            <h3>Quick Links</h3>
                            <Link to="/" className="footer-link">Home</Link>
                            <Link to="/map" className="footer-link">Live Map</Link>
                        </div>
                        <div className="footer-section">
                            <h3>Contact</h3>
                            <p className="footer-text">ASIET AI & IoT Lab, Kalady</p>
                        </div>
                    </div>
                    <div className="footer-bottom">
                        <p>&copy; 2025 AirAware. All rights reserved.</p>
                    </div>
                </div>
            </footer>
        </div>
    );
}

export default Dashboard;
