import React, { useEffect, useState, useCallback, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../App';
import './css/HomePage.css';

function HomePage() {
  // STRICT MODE: Only real sensor data, no fake fallbacks
  const [homeData, setHomeData] = useState({
    highest_sub_index: null,
    latest_item: null,
    station_name: 'ASIET Campus Station',
    status: 'initializing',
    has_real_sensor_data: false
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [lastFetchTime, setLastFetchTime] = useState(null);
  const [fetchCount, setFetchCount] = useState(0);

  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // STRICT MODE: Only fetch and display real sensor data
  // SIMPLE FRONTEND FIX - Replace your fetchHomeData function with this:

const fetchHomeData = useCallback(async () => {
    console.log('üîÑ Starting simple fetch...');
    
    setLoading(true);
    setFetchCount(prev => prev + 1);
    
    try {
      // STEP 1: Test basic connection
      const url = `${API_BASE_URL}/api/home/`;
      console.log(`üì° Fetching from: ${url}`);
      
      const response = await fetch(url);
      console.log(`üìä Response status: ${response.status}`);
      
      if (!response.ok) {
        throw new Error(`Server returned ${response.status}`);
      }
      
      const data = await response.json();
      console.log(`‚úÖ Got data:`, data);
      
      // STEP 2: Process the data simply
      setHomeData({
        highest_sub_index: data.highest_sub_index,
        latest_item: data.latest_item,
        station_name: data.station_name || 'ASIET Campus Station',
        status: data.status,
        has_real_sensor_data: data.has_real_sensor_data || false,
        total_items_processed: data.total_items_processed || 0
      });
      
      setError(null);
      setLastFetchTime(new Date());
      
      console.log(`‚úÖ Success! AQI: ${data.highest_sub_index}, Date: ${data.latest_item?.date}`);

    } catch (err) {
      console.error(`‚ùå Fetch failed:`, err);
      setError(`Cannot connect to server: ${err.message}`);
      
      // Set offline state
      setHomeData(prev => ({
        ...prev,
        status: 'connection_failed',
        has_real_sensor_data: false,
        highest_sub_index: null,
        latest_item: null
      }));
      
    } finally {
      setLoading(false);
    }
}, [API_BASE_URL]);

  // Particles animation (memoized)
  const particles = useMemo(() => {
    const particleElements = [];
    for (let i = 0; i < 15; i++) {
      particleElements.push(
        <div
          key={i}
          className="particle"
          style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            width: `${Math.random() * 6 + 4}px`,
            height: `${Math.random() * 6 + 4}px`,
            animationDelay: `${Math.random() * 8}s`,
          }}
        />
      );
    }
    return particleElements;
  }, []);

  // Set up data fetching
  useEffect(() => {
    console.log('üöÄ STRICT MODE: Component mounted - only real sensor data will be shown');
    
    fetchHomeData();
    
    const interval = setInterval(() => {
      console.log('‚è∞ STRICT MODE: Interval fetch - checking for real sensor data');
      fetchHomeData();
    }, 30000);

    return () => {
      console.log('üõë Cleaning up fetch interval');
      clearInterval(interval);
    };
  }, [fetchHomeData]);

  // STRICT AQI STATUS: Only for real data
  const aqiStatus = useMemo(() => {
    const getAQIStatus = (aqiValue, hasRealData) => {
      // Loading state
      if (loading && fetchCount <= 1) {
        return { status: 'Loading...', icon: 'fas fa-spinner fa-spin', color: '#9ca3af' };
      }
      
      // No real sensor data
      if (!hasRealData || aqiValue === null || aqiValue === undefined) {
        return { status: 'NO DATA', icon: 'fas fa-exclamation-triangle', color: '#ef4444' };
      }
      
      // Real sensor data - calculate status
      const value = parseInt(aqiValue, 10);
      if (value <= 50) return { status: 'GOOD', icon: 'fas fa-smile', color: '#10b981' };
      if (value <= 100) return { status: 'MODERATE', icon: 'fas fa-meh', color: '#f59e0b' };
      if (value <= 150) return { status: 'UNHEALTHY', icon: 'fas fa-frown', color: '#ef4444' };
      if (value <= 200) return { status: 'SEVERE', icon: 'fas fa-exclamation-triangle', color: '#dc2626' };
      if (value <= 300) return { status: 'VERY SEVERE', icon: 'fas fa-skull', color: '#7c2d12' };
      return { status: 'HAZARDOUS', icon: 'fas fa-biohazard', color: '#4c1d95' };
    };
    
    return getAQIStatus(homeData?.highest_sub_index, homeData?.has_real_sensor_data);
  }, [homeData?.highest_sub_index, homeData?.has_real_sensor_data, loading, fetchCount]);

  // Event handlers
  const toggleMenu = useCallback(() => setIsMenuOpen(prev => !prev), []);
  const handleMapNavigation = useCallback(() => navigate('/map'), [navigate]);
  const handleDashboardNavigation = useCallback(() => user ? navigate('/dashboard') : navigate('/login'), [user, navigate]);
  const handleLogout = useCallback(() => { logout(); navigate('/'); }, [logout, navigate]);

  // STRICT DISPLAY VALUES: Only real sensor data
  const displayAQI = (() => {
    const aqi = homeData?.highest_sub_index;
    const hasRealData = homeData?.has_real_sensor_data;
    
    if (loading && fetchCount <= 1) {
      return "...";
    }
    
    if (hasRealData === true && aqi !== null && aqi !== undefined) {
      console.log('‚úÖ Displaying REAL AQI:', aqi);
      return aqi;
    }
    
    console.log('‚ùå No real AQI data available');
    return "N/A";
  })();

  const displayLastUpdate = (() => {
    const latestItem = homeData?.latest_item;
    const hasRealData = homeData?.has_real_sensor_data;
    
    console.log('üîç STRICT UPDATE CHECK:', { latestItem, hasRealData });
    
    if (loading && fetchCount <= 1) {
      return "Loading sensor data...";
    }
    
    if (hasRealData === true && latestItem?.date && latestItem?.time) {
      console.log('‚úÖ Displaying REAL sensor timestamp:', latestItem.date, latestItem.time);
      return `${latestItem.date} at ${latestItem.time}`;
    }
    
    console.log('‚ùå No real sensor timestamp available');
    return "No sensor data available";
  })();

  const stationName = homeData?.station_name || 'ASIET Campus Station';

  // STRICT STATUS INDICATOR
  const getDataSourceInfo = () => {
    const hasRealData = homeData?.has_real_sensor_data;
    const status = homeData?.status;
    
    if (loading && fetchCount <= 1) {
      return { text: '‚è≥ Connecting to sensors...', color: '#6b7280' };
    }
    
    if (hasRealData === true) {
      return { text: 'üü¢ Live Sensor Data', color: '#10b981' };
    }
    
    if (status === 'sensors_offline' || status === 'no_sensor_timestamp') {
      return { text: 'üî¥ Sensors Offline', color: '#ef4444' };
    }
    
    if (error) {
      return { text: '‚ö†Ô∏è Connection Error', color: '#f59e0b' };
    }
    
    return { text: '‚ùå No Sensor Data', color: '#ef4444' };
  };

  const dataSourceInfo = getDataSourceInfo();

  return (
    <div className="homepage">
      {/* --- Navbar --- */}
      <nav className="navbar">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" loading="lazy" width="40" height="40" />
            AirAware
          </Link>
          <div className="menu-toggle" onClick={toggleMenu}>
            <i className="fas fa-bars"></i>
          </div>
          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><Link to="/" className="nav-link">Home</Link></li>
            <li><a href="#cities" className="nav-link">Cities</a></li>
            <li><a href="#services" className="nav-link">Services</a></li>
            <li><a href="#contact" className="nav-link">Contact</a></li>
            <li><Link to="/map" className="nav-link">Live Map</Link></li>
            {user ? (
              <>
                <li>
                  <button onClick={handleDashboardNavigation} className="nav-link nav-button">
                    Dashboard
                  </button>
                </li>
                <li className="nav-link user-greeting">Hello, {user.name}</li>
                <li>
                  <button onClick={handleLogout} className="nav-link login-btn nav-button">
                    <i className="fas fa-sign-out-alt"></i> Logout
                  </button>
                </li>
              </>
            ) : (
              <li>
                <Link to="/login" className="nav-link login-btn">
                  <i className="fas fa-sign-in-alt"></i> Login
                </Link>
              </li>
            )}
          </ul>
        </div>
      </nav>

      {/* --- Hero Section --- */}
      <section className="hero-section" id="home">
        <div className="hero-overlay"></div>
        <div className="particles">{particles}</div>
        <div className="hero-content">
          <h1 className="hero-title">Air Quality Monitoring</h1>
          <p className="hero-subtitle">Live air quality updates and insights from Kalady, Ernakulam</p>
          <p className="hero-note">Real-time data from {stationName}</p>
          
          <div className="aqi-display">
            <div className="aqi-label">
              Current AQI ‚Ä¢ Last Updated: <span>{displayLastUpdate}</span>
            </div>
            <div className="aqi-value" style={{ color: aqiStatus.color }}>
              {displayAQI}
            </div>
            <div className="aqi-status">AQI - {aqiStatus.status}</div>
            <div className="aqi-icon">
              <i className={aqiStatus.icon} style={{ color: aqiStatus.color }}></i>
            </div>
            
            {/* STRICT STATUS DISPLAY */}
            <div style={{ marginTop: '1rem', fontSize: '0.85rem' }}>
              {/* Data Source Status */}
              <div style={{ 
                color: dataSourceInfo.color, 
                marginBottom: '0.5rem', 
                fontWeight: '500' 
              }}>
                {dataSourceInfo.text}
              </div>
              
              {/* Show fetch info only if we have real data */}
              {homeData?.has_real_sensor_data && lastFetchTime && (
                <div style={{ 
                  color: '#94a3b8', 
                  fontSize: '0.75rem', 
                  marginBottom: '0.3rem' 
                }}>
                  Last checked: {lastFetchTime.toLocaleTimeString()} (Fetch #{fetchCount})
                </div>
              )}
              
              {/* Show processing info only if we have real data */}
              {homeData?.has_real_sensor_data && homeData?.total_items_processed > 0 && (
                <div style={{ 
                  color: '#64748b', 
                  fontSize: '0.7rem', 
                  marginBottom: '0.3rem' 
                }}>
                  üìä Processed {homeData.total_items_processed} sensor readings
                </div>
              )}
              
              {/* Error Display */}
              {error && (
                <div style={{
                  color: '#fbbf24', 
                  fontSize: '0.75rem', 
                  marginTop: '0.5rem'
                }}>
                  ‚ö†Ô∏è {error}
                </div>
              )}
              
              {/* Loading Indicator */}
              {loading && (
                <div style={{
                  color: '#60a5fa', 
                  fontSize: '0.75rem'
                }}>
                  üîÑ Checking for sensor data...
                </div>
              )}
              
              {/* No Data Message */}
              {!loading && !homeData?.has_real_sensor_data && (
                <div style={{
                  color: '#ef4444', 
                  fontSize: '0.75rem',
                  marginTop: '0.5rem'
                }}>
                  üî¥ Sensors are currently offline. Please try again later.
                </div>
              )}
            </div>
          </div>

          <button onClick={handleMapNavigation} className="cta-button">
            <i className="fas fa-map-marked-alt"></i> View Live AQI Map
          </button>
        </div>
      </section>

      {/* --- Stats Section --- */}
      <section className="stats-section">
        <div className="stats-container">
          <div className="section-header">
            <h2 className="section-title">Kerala Air Quality Network</h2>
            <p className="section-subtitle">
              Comprehensive monitoring across multiple locations for accurate air quality assessment
            </p>
          </div>
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-number">2</div>
              <div className="stat-label">Active Monitoring Stations</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">24/7</div>
              <div className="stat-label">Real-time Monitoring</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">7</div>
              <div className="stat-label">Key Pollutants Tracked</div>
            </div>
          </div>
        </div>
      </section>

      {/* --- AQI Scale Section --- */}
      <section className="section aqi-scale-section">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Understanding Air Quality Index</h2>
            <p className="section-subtitle">
              Know what the AQI numbers mean for your health and daily activities
            </p>
          </div>
          <div className="aqi-scale-grid">
            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-good"></div>
                <div className="aqi-range-text">Good (0-50)</div>
              </div>
              <div className="aqi-description">
                Air quality is satisfactory. Air pollution poses little or no risk for everyone.
              </div>
            </div>
            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-moderate"></div>
                <div className="aqi-range-text">Moderate (51-100)</div>
              </div>
              <div className="aqi-description">
                Air quality is acceptable. However, sensitive individuals may experience minor issues.
              </div>
            </div>
            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-unhealthy"></div>
                <div className="aqi-range-text">Unhealthy (101-200)</div>
              </div>
              <div className="aqi-description">
                Members of sensitive groups may experience health effects. Limit outdoor activities.
              </div>
            </div>
            <div className="aqi-scale-item">
              <div className="aqi-range">
                <div className="aqi-color-indicator aqi-hazardous"></div>
                <div className="aqi-range-text">Hazardous (201+)</div>
              </div>
              <div className="aqi-description">
                Health alert: The risk of health effects is increased for everyone. Avoid outdoor activities.
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* --- Cities Section --- */}
      <section className="section cities-section" id="cities">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Monitoring Locations</h2>
            <p className="section-subtitle">
              Air quality data from strategic locations across Ernakulam district
            </p>
          </div>
          <div className="cities-grid">
            <div className="city-card" onClick={handleMapNavigation}>
              <div className="city-icon">
                <i className="fas fa-university"></i>
              </div>
              <div className="city-content">
                <div className="city-name">ASIET Campus Station</div>
                <div className="city-type">Educational Institution</div>
                <div className="city-description">Main campus monitoring station</div>
              </div>
              <div className="city-action">
                <span>View on Map</span>
                <i className="fas fa-arrow-right"></i>
              </div>
            </div>
            <div className="city-card" onClick={handleMapNavigation}>
              <div className="city-icon">
                <i className="fas fa-city"></i>
              </div>
              <div className="city-content">
                <div className="city-name">Mattoor Junction Station</div>
                <div className="city-type">Urban Area</div>
                <div className="city-description">Urban junction monitoring station</div>
              </div>
              <div className="city-action">
                <span>View on Map</span>
                <i className="fas fa-arrow-right"></i>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* --- Services Section --- */}
      <section className="section services-section" id="services">
        <div className="section-container">
          <div className="section-header">
            <h2 className="section-title">Our Services</h2>
            <p className="section-subtitle">
              Advanced air quality solutions powered by cutting-edge technology
            </p>
          </div>
          <div className="services-grid">
            <div className="service-card">
              <div className="service-icon">
                <i className="fas fa-map-marked-alt"></i>
              </div>
              <h3 className="service-title">Interactive AQI Map</h3>
              <p className="service-description">
                Explore real-time air quality data across multiple monitoring stations with our 
                interactive map featuring live updates and forecasting.
              </p>
              <button onClick={handleMapNavigation} className="service-button">
                <i className="fas fa-arrow-right"></i>View Map
              </button>
            </div>
            <div className="service-card">
              <div className="service-icon">
                <i className="fas fa-heartbeat"></i>
              </div>
              <h3 className="service-title">Health Risk Assessment</h3>
              <p className="service-description">
                Receive personalized health recommendations based on current air quality 
                conditions and your individual health profile.
              </p>
              <button onClick={handleDashboardNavigation} className="service-button">
                <i className="fas fa-arrow-right"></i>Get Assessment
              </button>
            </div>
            <div className="service-card">
              <div className="service-icon">
                <i className="fas fa-chart-line"></i>
              </div>
              <h3 className="service-title">AQI Forecasting</h3>
              <p className="service-description">
                Get accurate air quality predictions with advanced meteorological models 
                and machine learning algorithms for better planning.
              </p>
              <button onClick={handleMapNavigation} className="service-button">
                <i className="fas fa-arrow-right"></i>View Forecast
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* --- Footer --- */}
      <footer className="footer" id="contact">
        <div className="footer-container">
          <div className="footer-grid">
            <div className="footer-section">
              <h3>About AirQuality Kerala</h3>
              <p className="footer-text">
                Leading provider of real-time air quality monitoring solutions in Kerala, 
                committed to environmental sustainability and public health awareness.
              </p>
              <div className="social-links">
                <a href="#" className="social-link">
                  <i className="fab fa-facebook-f"></i>
                </a>
                <a href="#" className="social-link">
                  <i className="fab fa-twitter"></i>
                </a>
                <a href="#" className="social-link">
                  <i className="fab fa-linkedin-in"></i>
                </a>
                <a href="#" className="social-link">
                  <i className="fab fa-instagram"></i>
                </a>
              </div>
            </div>
            <div className="footer-section">
              <h3>Quick Links</h3>
              <Link to="/" className="footer-link">Home</Link>
              <a href="#cities" className="footer-link">Monitoring Stations</a>
              <a href="#services" className="footer-link">Services</a>
              <Link to="/map" className="footer-link">Live Map</Link>
              {user && (
                <button onClick={handleLogout} className="footer-link footer-button">
                  Logout
                </button>
              )}
            </div>
            <div className="footer-section">
              <h3>Services</h3>
              <button onClick={handleMapNavigation} className="footer-link footer-button">
                Interactive Map
              </button>
              <button onClick={handleDashboardNavigation} className="footer-link footer-button">
                Health Assessment
              </button>
              <button onClick={handleMapNavigation} className="footer-link footer-button">
                AQI Forecasting
              </button>
              <a href="#" className="footer-link">Data Analytics</a>
            </div>
            <div className="footer-section">
              <h3>Contact Information</h3>
              <p className="footer-text">
                Adi Shankara Institute of Engineering and Technology<br />
                Kalady 683574, Ernakulam<br />
                Kerala, India
              </p>
              <br />
              <p className="footer-text">
                <strong>Email:</strong> aiiot@adishankara.ac.in<br />
                <strong>Phone:</strong> 0484 246 3825
              </p>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 AirQuality Kerala. All rights reserved. Developed by ASIET AI & IoT Lab.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default HomePage;
