import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './css/Dashboard.css';
import logoImage from '../assets/aqi.webp';

// Lazy loading components
const LazyMap = React.lazy(() =>
  import('./LazyMap').catch(() => ({
    default: () => (
      <div className="map-fallback">
        <div className="map-placeholder">
          <div className="map-icon">üó∫Ô∏è</div>
          <p>Interactive map loading...</p>
        </div>
      </div>
    )
  }))
);

const LazyChart = React.lazy(() =>
  import('./LazyChart').catch(() => ({
    default: () => (
      <div className="chart-fallback">
        <div className="chart-placeholder">
          <div className="chart-icon">üìä</div>
          <p>Chart loading...</p>
        </div>
      </div>
    )
  }))
);

// Utility Functions
const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 6371; // Earth's radius in km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c; 
};

// Known accurate locations for the area
const KNOWN_LOCATIONS = {
  '10.1783_76.4309': {
    display_name: 'ASIET Campus, Kalady',
    city: 'Kalady',
    state: 'Kerala',
    country: 'India'
  },
  '10.1710_76.4296': {
    display_name: 'Mattoor Junction, Kalady',
    city: 'Kalady',
    state: 'Kerala',
    country: 'India'
  },
  '10.1750_76.4300': {
    display_name: 'Kalady Town, Kerala',
    city: 'Kalady',
    state: 'Kerala',
    country: 'India'
  },
  '10.1765_76.4285': {
    display_name: 'Kalady Railway Station Area',
    city: 'Kalady',
    state: 'Kerala',
    country: 'India'
  },
  '10.1800_76.4700': {
    display_name: 'Perumbavoor, Kerala',
    city: 'Perumbavoor',
    state: 'Kerala',
    country: 'India'
  },
  '10.1900_76.3900': {
    display_name: 'Angamaly, Kerala',
    city: 'Angamaly',
    state: 'Kerala',
    country: 'India'
  }
};


// Enhanced location naming with reliable fallback (no external API calls)
const getLocationName = async (lat, lng) => {
  console.log('üîç Getting location name for:', lat, lng);

  // Strategy 1: Check known locations first (exact match)
  const coordKey = `${lat.toFixed(4)}_${lng.toFixed(4)}`;
  if (KNOWN_LOCATIONS[coordKey]) {
    console.log('‚úÖ Using exact known location:', KNOWN_LOCATIONS[coordKey].display_name);
    return KNOWN_LOCATIONS[coordKey];
  }

  // Strategy 2: Check nearby known locations (within 2km radius)
  for (const [key, location] of Object.entries(KNOWN_LOCATIONS)) {
    const [knownLat, knownLng] = key.split('_').map(Number);
    const distance = calculateDistance(lat, lng, knownLat, knownLng);
    if (distance < 2.0) {
      console.log(`‚úÖ Using nearby known location: ${location.display_name} (${distance.toFixed(2)}km away)`);
      return {
        ...location,
        display_name: distance < 0.5 ? location.display_name : `Near ${location.city}, ${location.state}`
      };
    }
  }

  // Strategy 3: Try ONLY BigDataCloud (most reliable, no API key needed)
  try {
    console.log('üîç Trying BigDataCloud geocoding...');
    const response = await fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${lat}&longitude=${lng}&localityLanguage=en`, {
      timeout: 5000
    });

    if (response.ok) {
      const data = await response.json();
      const location = parseBigDataCloudResult(data);
      if (location) {
        console.log('‚úÖ BigDataCloud geocoding successful:', location.display_name);
        return location;
      }
    }
  } catch (error) {
    console.warn('‚ö†Ô∏è BigDataCloud geocoding failed:', error.message);
  }

  // Strategy 4: Intelligent regional fallback based on coordinates
  const regionalFallback = getRegionalFallback(lat, lng);
  console.log('‚ö†Ô∏è Using regional fallback:', regionalFallback.display_name);
  return regionalFallback;
};

// Parse BigDataCloud result (only reliable service we're using)
const parseBigDataCloudResult = (data) => {
  if (!data || !data.locality) return null;
  return {
    display_name: `${data.locality}, ${data.principalSubdivision || 'Kerala'}`,
    city: data.locality,
    state: data.principalSubdivision || 'Kerala',
    country: data.countryName || 'India'
  };
};

// Enhanced regional fallback with more specific Kerala locations
const getRegionalFallback = (lat, lng) => {
  // Very specific area around Kalady/ASIET
  if (lat >= 10.17 && lat <= 10.19 && lng >= 76.42 && lng <= 76.45) {
    return {
      display_name: 'Kalady Area, Kerala',
      city: 'Kalady',
      state: 'Kerala',
      country: 'India'
    };
  }

  // Broader Ernakulam district area
  if (lat >= 9.8 && lat <= 10.4 && lng >= 76.1 && lng <= 76.6) {
    return {
      display_name: 'Ernakulam District, Kerala',
      city: 'Ernakulam District',
      state: 'Kerala',
      country: 'India'
    };
  }

  // Perumbavoor area
  if (lat >= 10.15 && lat <= 10.20 && lng >= 76.45 && lng <= 76.50) {
    return {
      display_name: 'Perumbavoor Area, Kerala',
      city: 'Perumbavoor',
      state: 'Kerala',
      country: 'India'
    };
  }

  // Angamaly area
  if (lat >= 10.18 && lat <= 10.22 && lng >= 76.35 && lng <= 76.42) {
    return {
      display_name: 'Angamaly Area, Kerala',
      city: 'Angamaly',
      state: 'Kerala',
      country: 'India'
    };
  }

  // General Kerala fallback
  if (lat >= 8.0 && lat <= 12.8 && lng >= 74.8 && lng <= 77.4) {
    return {
      display_name: 'Kerala, India',
      city: 'Kerala',
      state: 'Kerala',
      country: 'India'
    };
  }

  // Default fallback
  return {
    display_name: `Location in India (${lat.toFixed(3)}, ${lng.toFixed(3)})`,
    city: 'Unknown Location',
    state: 'India',
    country: 'India'
  };
};
const handleEnableLocation = useCallback(async () => {
  try {
    const location = await getUserLocation();
    await fetchDashboardData(location);
  } catch (error) {
    console.error('Failed to get location:', error);
  }
}, [getUserLocation, fetchDashboardData]);

function Dashboard() {
  // ===== STATE MANAGEMENT =====
  const [user, setUser] = useState(() => {
    try {
      const storedUser = localStorage.getItem('user');
      return storedUser ? JSON.parse(storedUser) : null;
    } catch {
      return null;
    }
  });

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStationInfo, setNearestStationInfo] = useState(null);
  const [currentDataInfo, setCurrentDataInfo] = useState(null);
  const [locationStatus, setLocationStatus] = useState('initializing');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isLocationUpdating, setIsLocationUpdating] = useState(false);
  const [lastUpdateTime, setLastUpdateTime] = useState(new Date());
  const [healthData, setHealthData] = useState(null);
  const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);

  const navigate = useNavigate();
  const API_BASE_URL = process.env.NODE_ENV === 'production'
    ? 'https://airaware-app-gcw7.onrender.com'
    : 'http://localhost:8000';
  const abortControllerRef = useRef(null);

  // ===== MOBILE DETECTION AND RESIZE HANDLER =====
  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth <= 768;
      setIsMobileView(mobile);
      if (!mobile && isMenuOpen) {
        setIsMenuOpen(false);
        document.body.style.overflow = '';
      }
    };
    let resizeTimeout;
    const debouncedResize = () => {
      clearTimeout(resizeTimeout);
      resizeTimeout = setTimeout(handleResize, 100);
    };
    window.addEventListener('resize', debouncedResize);
    handleResize();
    return () => {
      window.removeEventListener('resize', debouncedResize);
      clearTimeout(resizeTimeout);
    };
  }, [isMenuOpen]);

  // ===== MOBILE NAVIGATION HANDLERS =====
  const toggleMenu = useCallback(() => {
    setIsMenuOpen(prev => {
      const newState = !prev;
      document.body.style.overflow = newState ? 'hidden' : '';
      return newState;
    });
  }, []);

  const closeMenu = useCallback(() => {
    setIsMenuOpen(false);
    document.body.style.overflow = '';
  }, []);

  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape' && isMenuOpen) {
        closeMenu();
      }
    };
    const handleClickOutside = (e) => {
      if (isMenuOpen && isMobileView) {
        const navLinks = document.querySelector('.nav-links');
        const menuToggle = document.querySelector('.menu-toggle');
        if (navLinks && !navLinks.contains(e.target) &&
            menuToggle && !menuToggle.contains(e.target)) {
          closeMenu();
        }
      }
    };
    document.addEventListener('keydown', handleEscape);
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.removeEventListener('click', handleClickOutside);
    };
  }, [isMenuOpen, isMobileView, closeMenu]);

  // ===== HIGH-ACCURACY LOCATION DETECTION =====
  const getUserLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation is not supported by your browser.'));
        return;
      }

      setLocationStatus('detecting');
      setIsLocationUpdating(true);

      console.log('üîç Starting high-accuracy location detection...');

      let attemptCount = 0;
      const maxAttempts = 3;
      let bestLocation = null;

      const tryGetLocation = () => {
        attemptCount++;
        console.log(`üìç Location attempt ${attemptCount}/${maxAttempts}`);

        const timeout = setTimeout(() => {
          console.warn(`‚è∞ Attempt ${attemptCount} timed out`);
          if (attemptCount < maxAttempts) {
            tryGetLocation(); // Retry with next attempt
          } else {
            setLocationStatus('failed');
            setIsLocationUpdating(false);
            reject(new Error('Location detection timed out after multiple attempts.'));
          }
        }, attemptCount === 1 ? 15000 : 20000); // Longer timeout for retries

        navigator.geolocation.getCurrentPosition(
          async (position) => {
            clearTimeout(timeout);

            const location = {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
              accuracy: position.coords.accuracy,
              source: 'gps',
              timestamp: Date.now(),
              attempt: attemptCount
            };

            console.log(`üìç Attempt ${attemptCount} - Coordinates: ${location.lat}, ${location.lng}, Accuracy: ${location.accuracy}m`);

            // Validate coordinates are in reasonable range for India/Kerala
            if (isNaN(location.lat) || isNaN(location.lng) || 
                location.lat < 8 || location.lat > 37 || 
                location.lng < 68 || location.lng > 97) {
              console.warn(`‚ùå Invalid coordinates for India: ${location.lat}, ${location.lng}`);

              if (attemptCount < maxAttempts) {
                tryGetLocation(); // Retry
                return;
              } else {
                reject(new Error('Received invalid GPS coordinates.'));
                return;
              }
            }

            // Check if this is a better location (more accurate)
            if (!bestLocation || location.accuracy < bestLocation.accuracy) {
              bestLocation = location;
              console.log(`‚úÖ New best location found! Accuracy: ${location.accuracy}m`);
            }

            // If accuracy is good enough (< 50m) or this is the last attempt, use it
            if (location.accuracy < 50 || attemptCount >= maxAttempts) {
              try {
                const locationName = await getLocationName(bestLocation.lat, bestLocation.lng);
                setUserLocationName(locationName);
                console.log('üìç Location name resolved:', locationName.display_name);
              } catch (nameError) {
                console.warn('‚ö†Ô∏è Failed to get location name, using fallback:', nameError);
                const fallbackName = getRegionalFallback(bestLocation.lat, bestLocation.lng);
                setUserLocationName(fallbackName);
              }

              setUserLocation(bestLocation);
              setLocationStatus('gps_detected');
              setIsLocationUpdating(false);
              console.log(`üéØ Final location: ${bestLocation.lat}, ${bestLocation.lng} (${bestLocation.accuracy}m accuracy)`);
              resolve(bestLocation);
            } else if (attemptCount < maxAttempts) {
              // Try again for better accuracy
              console.log(`üîÑ Accuracy ${location.accuracy}m not good enough, trying again...`);
              setTimeout(tryGetLocation, 1000); // Wait 1 second before retry
            }
          },
          (error) => {
            clearTimeout(timeout);
            console.error(`‚ùå Geolocation attempt ${attemptCount} failed:`, error.message);

            if (attemptCount < maxAttempts) {
              // Try with less strict settings on retry
              setTimeout(tryGetLocation, 2000); // Wait 2 seconds before retry
            } else {
              setLocationStatus('failed');
              setIsLocationUpdating(false);
              reject(new Error(`Location detection failed after ${maxAttempts} attempts: ${error.message}`));
            }
          },
          {
            enableHighAccuracy: true,
            timeout: attemptCount === 1 ? 12000 : 15000, // Longer timeout for retries
            maximumAge: 0 // Always get fresh location
          }
        );
      };

      // Start the first attempt
      tryGetLocation();
    });
  }, []);

  // ===== PROCESS DASHBOARD DATA =====
  const processDashboardData = useCallback((data, locationData) => {
    if (!data || !data.stations) {
      console.warn('No station data received');
      return;
    }
    setDashboardData(data);

    const stations = data.stations;
    const stationIds = Object.keys(stations);
    if (locationData && stationIds.length > 0) {
      const stationDistances = {};
      stationIds.forEach(stationId => {
        const station = stations[stationId];
        const distance = calculateDistance(
          locationData.lat,
          locationData.lng,
          station.station_info.lat,
          station.station_info.lng
        );
        stationDistances[stationId] = {
          distance: distance,
          station: station
        };
      });
      const nearestStationId = Object.keys(stationDistances).reduce((nearest, current) =>
        stationDistances[current].distance < stationDistances[nearest].distance ? current : nearest
      );
      const nearestDistance = stationDistances[nearestStationId].distance;
      const nearestStation = stationDistances[nearestStationId].station;
      setNearestStationInfo({
        id: nearestStationId,
        name: nearestStation.station_info.name,
        distance: nearestDistance,
        aqi: nearestStation.highest_sub_index || 50
      });
      const isWithinSensorRange = Object.values(stationDistances).some(s => s.distance <= 2.0);
      if (isWithinSensorRange || nearestDistance <= 1.0) {
        const idwResult = calculateIDWInterpolation(locationData, stations);
        setCurrentDataInfo({
          method: 'location_interpolation',
          source: 'interpolated',
          values: idwResult.interpolated_values,
          aqi: idwResult.interpolated_aqi,
          station_name: `Your Location (${userLocationName?.city || 'Current Position'})`,
          is_interpolated: true,
          show_distance_message: true,
          distance_message: `üìç You are within sensor range (${nearestDistance.toFixed(1)}km from nearest), showing calculated values for your exact location`,
          data_type: 'Your Location Data (Calculated)'
        });
      } else {
        setCurrentDataInfo({
          method: 'nearest_station',
          source: 'nearest_station',
          values: nearestStation.averages || {},
          aqi: nearestStation.highest_sub_index || 50,
          station_name: nearestStation.station_info.name,
          is_interpolated: false,
          show_distance_message: true,
          distance_message: `üìç You are ${nearestDistance.toFixed(1)}km from the nearest sensor node, so you are seeing data from ${nearestStation.station_info.name}`,
          data_type: 'Nearest Station Data'
        });
      }
    } else {
      const defaultStation = stations['lora-v1'] || stations[stationIds[0]] || {};
      setCurrentDataInfo({
        method: 'default_station',
        source: '',
        explanation: '',
        values: defaultStation.averages || {},
        aqi: defaultStation.highest_sub_index || 50,
        station_name: defaultStation.station_info?.name || 'ASIET Campus Station',
        is_interpolated: false,
        show_distance_message: false,
        distance_message: null,
        data_type: ' Station Data'
      });
    }
    console.log('üìä Dashboard data processed successfully');
  }, [userLocationName]);

  // ===== FETCH DASHBOARD DATA =====
  const fetchDashboardData = useCallback(async (locationData = null) => {
    if (!user || !user.user_id) {
      navigate('/login');
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const controller = new AbortController();
      abortControllerRef.current = controller;

      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('user_id', user.user_id);
      if (locationData) {
        url.searchParams.append('lat', locationData.lat.toString());
        url.searchParams.append('lng', locationData.lng.toString());
      }

      const response = await fetch(url, { signal: controller.signal });
      if (!response.ok) {
        const errData = await response.json();
        if (errData.redirect_to) navigate(errData.redirect_to);
        throw new Error(errData.error || `HTTP ${response.status}`);
      }

      const data = await response.json();
      setHealthData(data.health_data);
      processDashboardData(data, locationData);
      setLastUpdateTime(new Date());
    } catch (error) {
      if (error.name !== 'AbortError') setError("Failed to load dashboard. Please try again.");
    } finally {
      setLoading(false);
    }
  }, [user, navigate, API_BASE_URL, processDashboardData]);

  // ===== IDW INTERPOLATION CALCULATION =====
  const calculateIDWInterpolation = useCallback((locationData, stations) => {
    const stationIds = Object.keys(stations);
    let totalWeight = 0;
    const weightedValues = {
      pm25: 0, pm10: 0, so2: 0, no2: 0,
      co: 0, o3: 0, nh3: 0, temp: 0, hum: 0, pre: 0
    };
    let weightedAqi = 0;
    stationIds.forEach(stationId => {
      const station = stations[stationId];
      const distance = calculateDistance(
        locationData.lat,
        locationData.lng,
        station.station_info.lat,
        station.station_info.lng
      );
      const safeDistance = Math.max(distance, 0.001);
      const weight = 1.0 / (safeDistance ** 2);
      totalWeight += weight;
      const averages = station.averages || {};
      Object.keys(weightedValues).forEach(param => {
        if (averages[param] !== undefined) {
          weightedValues[param] += averages[param] * weight;
        }
      });
      weightedAqi += (station.highest_sub_index || 0) * weight;
    });
    const interpolated_values = {};
    Object.keys(weightedValues).forEach(param => {
      interpolated_values[param] = totalWeight > 0 ?
        Math.round((weightedValues[param] / totalWeight) * 100) / 100 : 0;
    });
    const interpolated_aqi = totalWeight > 0 ?
      Math.round(weightedAqi / totalWeight) : 50;
    return {
      interpolated_values,
      interpolated_aqi,
      stations_used: stationIds.length,
      method: 'idw'
    };
  }, []);

  // ===== LOAD SAMPLE DATA =====
  const loadSampleData = useCallback(() => {
    console.log('üîÑ Loading sample data...');
    const sampleData = {
      stations: {
        'lora-v1': {
          station_info: { name: 'ASIET Campus Station', lat: 10.178322, lng: 76.430891, description: 'Educational Institution Area' },
          averages: { pm25: 25, pm10: 42, so2: 8, no2: 40, co: 1.1, o3: 46, nh3: 93, temp: 28, hum: 65, pre: 1013 },
          highest_sub_index: 50,
          last_updated_on: 'Sample Data'
        },
        'loradev2': {
          station_info: { name: 'Mattoor Junction Station', lat: 10.170950, lng: 76.429628, description: 'Urban Commercial Area' },
          averages: { pm25: 22, pm10: 38, so2: 7, no2: 35, co: 1.0, o3: 43, nh3: 88, temp: 27, hum: 68, pre: 1012 },
          highest_sub_index: 45,
          last_updated_on: 'Sample Data'
        }
      },
      forecasts: {
        'lora-v1': [
          { day: 'Today', pm25_max: 30, pm10_max: 37, so2_max: 8, no2_max: 40, co_max: 1.1, o3_max: 46, nh3_max: 93 },
          { day: 'Tomorrow', pm25_max: 24, pm10_max: 34, so2_max: 8, no2_max: 45, co_max: 1.2, o3_max: 43, nh3_max: 100 },
          { day: 'Day 3', pm25_max: 24, pm10_max: 30, so2_max: 8, no2_max: 42, co_max: 1.2, o3_max: 45, nh3_max: 100 },
          { day: 'Day 4', pm25_max: 20, pm10_max: 23, so2_max: 8, no2_max: 38, co_max: 1.2, o3_max: 47, nh3_max: 100 }
        ],
        'loradev2': [
          { day: 'Today', pm25_max: 28, pm10_max: 35, so2_max: 7, no2_max: 38, co_max: 1.0, o3_max: 44, nh3_max: 90 },
          { day: 'Tomorrow', pm25_max: 22, pm10_max: 32, so2_max: 7, no2_max: 42, co_max: 1.1, o3_max: 41, nh3_max: 95 },
          { day: 'Day 3', pm25_max: 22, pm10_max: 28, so2_max: 7, no2_max: 40, co_max: 1.1, o3_max: 43, nh3_max: 95 },
          { day: 'Day 4', pm25_max: 18, pm10_max: 21, so2_max: 7, no2_max: 36, co_max: 1.1, o3_max: 45, nh3_max: 95 }
        ]
      },
      health_data: { risk_level: 'Low', score: 75, recommendations: ['Enjoy outdoor activities', 'Open windows for fresh air'] }
    };
    setHealthData(sampleData.health_data);
    processDashboardData(sampleData, userLocation);
    setLastUpdateTime(new Date());
    console.log('Sample data loaded successfully');
  }, [userLocation, processDashboardData]);

  // ===== INITIALIZATION =====
  useEffect(() => {
    const initializeDashboard = async () => {
      let location = null;
      try {
        location = await getUserLocation();
      } catch (e) {
        console.warn("Could not get user location on init.");
      }
      await fetchDashboardData(location);
    };
    initializeDashboard();
    return () => {
      if (abortControllerRef.current) abortControllerRef.current.abort();
    };
  }, [getUserLocation, fetchDashboardData]);

  const handleParameterChange = useCallback((param) => {
    setSelectedParameter(param);
  }, []); 

  // ===== MEMOIZED VALUES =====
  const currentValues = useMemo(() => currentDataInfo?.values || {}, [currentDataInfo]);
  const currentAQI = useMemo(() => currentDataInfo?.aqi || 50, [currentDataInfo]);
  const aqiStatus = useMemo(() => {
    const aqi = currentAQI;
    if (aqi <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
