import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../App';
import './css/FamilyPage.css';

function FamilyPage() {
    const { user } = useAuth();
    const [username] = useState(user?.name || null);
    const [familyMembers, setFamilyMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingMember, setEditingMember] = useState(null);
    
    // Form state
    const [formState, setFormState] = useState({ name: '', age: '', relationship: '' });
    const [formError, setFormError] = useState('');

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

    const fetchFamilyMembers = useCallback(async () => {
        // ... (fetch logic remains the same)
    }, [username, navigate, API_BASE_URL]);

    useEffect(() => {
        fetchFamilyMembers();
    }, [fetchFamilyMembers]);
    
    // --- HANDLER FUNCTIONS FOR ACTIONS ---
    const handleAddMemberClick = () => {
        setEditingMember(null);
        setFormState({ name: '', age: '', relationship: '' });
        setIsModalOpen(true);
    };

    const handleEditMemberClick = (member) => {
        setEditingMember(member);
        setFormState({ name: member.name, age: member.age, relationship: member.relationship });
        setIsModalOpen(true);
    };

    const handleDeleteMember = async (memberId) => {
        if (window.confirm("Are you sure you want to delete this family member?")) {
            try {
                await fetch(`${API_BASE_URL}/api/family-members/${memberId}/`, { method: 'DELETE' });
                setFamilyMembers(prev => prev.filter(m => m.id !== memberId));
            } catch (err) {
                alert("Failed to delete member.");
            }
        }
    };
    
    const handleFormSubmit = async (e) => {
        e.preventDefault();
        setFormError('');
        const url = editingMember 
            ? `${API_BASE_URL}/api/family-members/update/${editingMember.id}/`
            : `${API_BASE_URL}/api/family-members/`;
        const method = editingMember ? 'PUT' : 'POST';

        try {
            const response = await fetch(url, {
                method: method,
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ ...formState, username })
            });
            if (!response.ok) {
                const errData = await response.json();
                throw new Error(errData.error);
            }
            const updatedMember = await response.json();
            if (editingMember) {
                setFamilyMembers(prev => prev.map(m => m.id === updatedMember.id ? updatedMember : m));
            } else {
                setFamilyMembers(prev => [...prev, updatedMember]);
            }
            setIsModalOpen(false);
        } catch (err) {
            setFormError(err.message);
        }
    };

    if (isLoading) return <div className="panel-loader"><h2>Loading...</h2></div>;
    // ... (error handling)

    return (
        <div className="family-page">
            <nav className="navbar">{/* --- Your Navbar --- */}</nav>
            <div className="container">
                <div className="dashboard">
                    <section className="profile-card">{/* ... profile details ... */}</section>
                    <section className="members-section">
                        <div className="section-header">
                            <h2 className="section-title">Family Members</h2>
                            <button className="add-btn" onClick={handleAddMemberClick}>
                                <span>+</span> Add Member
                            </button>
                        </div>
                        {familyMembers.length > 0 ? (
                            <div className="members-grid">
                                {familyMembers.map(member => (
                                    <div className="member-card" key={member.id}>
                                        <div className="member-avatar">{member.name.charAt(0)}</div>
                                        <div className="member-info">
                                            <div className="member-name">{member.name}</div>
                                            <div className="member-relation">{member.relationship}</div>
                                            <div className="member-age">{member.age} years old</div>
                                            {/* --- ACTION BUTTONS ADDED --- */}
                                            <div className="member-actions">
                                                <Link to={`/health-assessment?username=${member.name}`} className="action-btn health-btn">Health Info</Link>
                                                <button className="action-btn edit-btn" onClick={() => handleEditMemberClick(member)}>Edit</button>
                                                <button className="action-btn delete-btn" onClick={() => handleDeleteMember(member.id)}>Delete</button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="no-members">{/* ... */}</div>
                        )}
                    </section>
                </div>
            </div>

            {isModalOpen && (
                <div className="modal-overlay">
                    <div className="modal">
                        <div className="modal-header">
                            <h3 className="modal-title">{editingMember ? 'Edit' : 'Add'} Family Member</h3>
                            <button className="close-btn" onClick={() => setIsModalOpen(false)}>&times;</button>
                        </div>
                        <form onSubmit={handleFormSubmit}>
                            {formError && <div className="form-error">{formError}</div>}
                            <div className="form-group">
                                <label>Full Name</label>
                                <input type="text" value={formState.name} onChange={(e) => setFormState({...formState, name: e.target.value})} />
                            </div>
                            <div className="form-group">
                                <label>Age</label>
                                <input type="number" value={formState.age} onChange={(e) => setFormState({...formState, age: e.target.value})} />
                            </div>
                            <div className="form-group">
                                <label>Relationship</label>
                                <select value={formState.relationship} onChange={(e) => setFormState({...formState, relationship: e.target.value})}>
                                    <option value="">Select Relationship</option>
                                    <option value="Father">Father</option>
                                    <option value="Mother">Mother</option>
                                    <option value="Sibling">Sibling</option>
                                    <option value="Spouse">Spouse</option>
                                    <option value="Child">Child</option>
                                    <option value="Other">Other</option>
                                </select>
                            </div>
                            <button type="submit" className="btn">{editingMember ? 'Save Changes' : 'Add Member'}</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

export default FamilyPage;
