import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { useAuth } from '../App';
import './css/MapPage.css';

// Register the necessary Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const getAQIColor = (aqi) => {
  if (aqi === null || aqi === undefined) return '#6b7280';
  if (aqi <= 50) return '#10b981';
  if (aqi <= 100) return '#f59e0b';
  if (aqi <= 200) return '#ef4444';
  return '#7c2d12';
};

const MapPage = () => {
  const [map, setMap] = useState(null);
  const [stations, setStations] = useState({});
  const [selectedStationId, setSelectedStationId] = useState(null);
  const [forecastData, setForecastData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isForecastLoading, setIsForecastLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isPanelOpen, setIsPanelOpen] = useState(true);

  const mapRef = useRef(null);
  const markersRef = useRef({});
  const navigate = useNavigate();
  const { user } = useAuth();
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  useEffect(() => {
    let isMounted = true;
    if (mapRef.current && !map) {
      const L = window.L; // Access Leaflet from the global scope
      const mapInstance = L.map(mapRef.current, {
        zoomControl: false,
        attributionControl: false,
      }).setView([10.176, 76.430], 14);
      
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: 'Â© OpenStreetMap contributors'
      }).addTo(mapInstance);
      
      if (isMounted) {
        setMap(mapInstance);
      }
    }
    return () => { isMounted = false; };
  }, [map]);

  const fetchRealtimeData = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/api/map/realtime/`);
      if (!response.ok) throw new Error(`Server error: ${response.status}`);
      const data = await response.json();
      setStations(data.stations);
      if (!selectedStationId && Object.keys(data.stations).length > 0) {
        setSelectedStationId(Object.keys(data.stations)[0]);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  }, [API_BASE_URL, selectedStationId]);

  useEffect(() => {
    fetchRealtimeData();
    const interval = setInterval(fetchRealtimeData, 30000);
    return () => clearInterval(interval);
  }, [fetchRealtimeData]);

  useEffect(() => {
    if (!selectedStationId) return;
    const fetchForecast = async () => {
      setIsForecastLoading(true);
      try {
        const response = await fetch(`${API_BASE_URL}/api/station/${selectedStationId}/forecast/`);
        if (!response.ok) throw new Error(`Forecast error: ${response.status}`);
        const data = await response.json();
        setForecastData(data.forecast_data || []);
      } catch (err) {
        console.error("Forecast fetch error:", err);
        setForecastData([]);
      } finally {
        setIsForecastLoading(false);
      }
    };
    fetchForecast();
  }, [selectedStationId, API_BASE_URL]);

  useEffect(() => {
    if (!map || Object.keys(stations).length === 0) return;
    const L = window.L;

    Object.entries(stations).forEach(([id, station]) => {
      const { lat, lng } = station.station_info;
      const aqi = station.highest_sub_index;
      
      const markerOptions = {
        radius: selectedStationId === id ? 12 : 8,
        fillColor: getAQIColor(aqi),
        color: selectedStationId === id ? '#ffffff' : '#1e293b',
        weight: selectedStationId === id ? 3 : 1.5,
        opacity: 1,
        fillOpacity: 0.85
      };

      if (markersRef.current[id]) {
        markersRef.current[id].setLatLng([lat, lng]).setStyle(markerOptions);
      } else {
        const marker = L.circleMarker([lat, lng], markerOptions).addTo(map);
        marker.on('click', () => {
          setSelectedStationId(id);
          setIsPanelOpen(true);
          map.setView([lat, lng], 16);
        });
        markersRef.current[id] = marker;
      }
    });
  }, [map, stations, selectedStationId]);

  const selectedStationData = stations[selectedStationId];

  return (
    <div className="map-page">
      <nav className="navbar">
          <div className="navbar-content">
              <Link to="/" className="navbar-brand">
                  <img src="/aqi.webp" alt="Logo" width="32" height="32" />
                  AirAware
              </Link>
              <ul className="nav-links">
                  <li><Link to="/" className="nav-link">Home</Link></li>
                  <li><Link to="/map" className="nav-link active">Live Map</Link></li>
                  {user ? (
                      <li><Link to="/dashboard" className="nav-link">Dashboard</Link></li>
                  ) : (
                      <li><Link to="/login" className="nav-link login-btn">Login</Link></li>
                  )}
              </ul>
          </div>
      </nav>
      
      <div className="main-content">
        <div className="map-container">
          <div id="map" ref={mapRef} className="map-element"></div>
          <button 
            className="panel-toggle panel-toggle-closed"
            onClick={() => setIsPanelOpen(true)}
            title="Show Panel"
          >
            <i className="fas fa-chevron-left"></i>
          </button>
        </div>
        
        <div className={`details-panel ${isPanelOpen ? 'open' : ''}`}>
          <button 
            className="panel-toggle panel-toggle-open"
            onClick={() => setIsPanelOpen(false)}
            title="Hide Panel"
          >
            <i className="fas fa-chevron-right"></i>
          </button>
          
          <div className="panel-header">
            <h2 className="panel-title">
              <i className="fas fa-map-marker-alt"></i> Monitoring Stations
            </h2>
            <div className="station-selector">
              {Object.entries(stations).map(([id, station]) => (
                <button 
                  key={id} 
                  className={`station-btn ${selectedStationId === id ? 'active' : ''}`} 
                  onClick={() => setSelectedStationId(id)}
                >
                  <div className="station-btn-content">
                    <span className="station-name">{station.station_info.name}</span>
                    <span className="station-aqi" style={{ color: getAQIColor(station.highest_sub_index) }}>
                      {Math.round(station.highest_sub_index) || 'N/A'}
                    </span>
                  </div>
                </button>
              ))}
            </div>
          </div>
          
          <div className="panel-content">
            {isLoading && (
              <div className="panel-loader">
                <div className="loading-spinner"></div>
                <p>Loading station data...</p>
              </div>
            )}
            {error && <div className="error-message">Error: {error}</div>}
            {selectedStationData && (
              <div>
                {/* You can add current AQI, Pollutant Metrics, and Forecast sections here */}
                <h3>{selectedStationData.station_info.name}</h3>
                <p>Current AQI: {Math.round(selectedStationData.highest_sub_index)}</p>
                {isForecastLoading && <p>Loading forecast...</p>}
                {forecastData.length > 0 && (
                  <div>
                    <h4>4-Day Forecast</h4>
                    {/* Render your forecast data here */}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default MapPage;
