import React, { useState } from 'react';
import './css/Login.css';

function Login() {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [showOTP, setShowOTP] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // API Base URL
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // Create particles effect
  const createParticles = () => {
    const particles = [];
    for (let i = 0; i < 50; i++) {
      particles.push(
        <div
          key={i}
          className="particle"
          style={{
            left: Math.random() * 100 + '%',
            top: Math.random() * 100 + '%',
            width: Math.random() * 10 + 5 + 'px',
            height: Math.random() * 10 + 5 + 'px',
            animationDelay: Math.random() * 5 + 's'
          }}
        />
      );
    }
    return particles;
  };

  // Clear messages
  const clearMessages = () => {
    setError('');
    setSuccess('');
  };

  // Validate and format phone number
  const validatePhoneNumber = (phone) => {
    // Remove all non-digit characters
    const cleanPhone = phone.replace(/\D/g, '');
    
    // Check if it's a valid Indian phone number
    if (cleanPhone.length === 10 && cleanPhone.match(/^[6-9]/)) {
      return cleanPhone; // Return without +91 prefix
    } else if (cleanPhone.length === 12 && cleanPhone.startsWith('91')) {
      return cleanPhone.substring(2); // Remove 91 prefix
    } else if (cleanPhone.length === 13 && cleanPhone.startsWith('91')) {
      return cleanPhone.substring(2); // Remove 91 prefix
    }
    
    return null;
  };

  // Handle phone number input
  const handlePhoneNumberChange = (e) => {
    const value = e.target.value;
    setPhoneNumber(value);
    clearMessages();
  };

  // Handle OTP input
  const handleOTPChange = (e) => {
    const value = e.target.value.replace(/\D/g, '').slice(0, 6);
    setOtp(value);
    clearMessages();
  };

  // Handle sending OTP
  const handleSendOTP = async (e) => {
    e.preventDefault();
    setLoading(true);
    clearMessages();

    console.log('Attempting to send OTP for phone:', phoneNumber);

    // Validate phone number
    const validatedPhone = validatePhoneNumber(phoneNumber);
    if (!validatedPhone) {
      setError('Please enter a valid 10-digit Indian mobile number');
      setLoading(false);
      return;
    }

    try {
      const requestData = { phone_number: validatedPhone };
      console.log('Sending request:', requestData);

      const response = await fetch(`${API_BASE_URL}/api/send-otp/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData),
      });

      console.log('Response status:', response.status);
      const data = await response.json();
      console.log('Response data:', data);

      if (response.ok) {
        setShowOTP(true);
        setSuccess('OTP sent successfully! Check your phone.');
        // Update phone number to the validated format
        setPhoneNumber(validatedPhone);
      } else {
        // Handle specific error cases
        if (response.status === 400) {
          setError(data.error || data.message || 'Invalid request. Please check your phone number.');
        } else if (response.status === 500) {
          setError('Server error. Please try again later.');
        } else {
          setError(data.error || 'Failed to send OTP. Please try again.');
        }
      }
    } catch (err) {
      console.error('Network error:', err);
      setError('Network error. Please check your connection and try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle OTP verification
  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    setLoading(true);
    clearMessages();

    // Validate OTP
    if (!otp || otp.length !== 6) {
      setError('Please enter a valid 6-digit OTP');
      setLoading(false);
      return;
    }

    try {
      const requestData = { 
        otp_code: otp, 
        phone_number: phoneNumber 
      };
      console.log('Verifying OTP:', requestData);

      const response = await fetch(`${API_BASE_URL}/api/verify-otp/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData),
      });

      console.log('Verify response status:', response.status);
      const data = await response.json();
      console.log('Verify response data:', data);

      if (response.ok) {
        // Store user data
        localStorage.setItem('user', JSON.stringify(data.user));
        localStorage.setItem('isLoggedIn', 'true');
        
        setSuccess('Login successful! Redirecting...');
        
        // Redirect based on backend response
        setTimeout(() => {
          if (data.redirect_to === '/health-assessment') {
            window.location.href = '/health-assessment';
          } else {
            window.location.href = '/dashboard';
          }
        }, 1500);
      } else {
        if (response.status === 400) {
          setError(data.error || 'Invalid OTP or expired. Please try again.');
        } else {
          setError(data.error || 'Verification failed. Please try again.');
        }
      }
    } catch (err) {
      console.error('Verification error:', err);
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle back to phone number
  const handleBackToPhone = () => {
    setShowOTP(false);
    setOtp('');
    clearMessages();
  };

  // Handle resend OTP
  const handleResendOTP = async () => {
    setLoading(true);
    clearMessages();
    
    try {
      const response = await fetch(`${API_BASE_URL}/api/send-otp/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ phone_number: phoneNumber }),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccess('OTP resent successfully!');
        setOtp('');
      } else {
        setError(data.error || 'Failed to resend OTP');
      }
    } catch (err) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-content">
          <a href="/" className="navbar-brand">
            <img src="/aqi.webp" alt="AQM Logo" />
            AirAware
          </a>
        </div>
      </nav>

      {/* Login Section */}
      <section className="login-section">
        <div className="particles">
          {createParticles()}
        </div>
        
        <div className="login-container">
          <div className="login-header">
            <h2>
              <i className="fas fa-user-circle"></i> 
              {showOTP ? 'Verify OTP' : 'Login'}
            </h2>
            <p>
              {showOTP 
                ? `Enter the 6-digit OTP sent to ${phoneNumber}` 
                : 'Welcome back! Please log in to your account.'
              }
            </p>
          </div>

          {/* Show success message */}
          {success && (
            <div className="success-message">
              <i className="fas fa-check-circle"></i>
              {success}
            </div>
          )}

          {/* Show error message */}
          {error && (
            <div className="error-message">
              <i className="fas fa-exclamation-triangle"></i>
              {error}
            </div>
          )}

          {!showOTP ? (
            // Phone Number Form
            <form onSubmit={handleSendOTP}>
              <div className="form-group">
                <label htmlFor="phone_number">Enter your phone number</label>
                <input
                  type="tel"
                  id="phone_number"
                  value={phoneNumber}
                  onChange={handlePhoneNumberChange}
                  placeholder="Enter 10-digit mobile number"
                  required
                  disabled={loading}
                  maxLength="13"
                />
                <small className="form-hint">
                  Enter your 10-digit mobile number (e.g., 9876543210)
                </small>
              </div>
              <button type="submit" className="btn-primary" disabled={loading || !phoneNumber}>
                {loading ? (
                  <>
                    <i className="fas fa-spinner fa-spin"></i> Sending OTP...
                  </>
                ) : (
                  'Send OTP'
                )}
              </button>
            </form>
          ) : (
            // OTP Verification Form
            <form onSubmit={handleVerifyOTP}>
              <div className="form-group">
                <label htmlFor="otp">Enter OTP</label>
                <input
                  type="text"
                  id="otp"
                  value={otp}
                  onChange={handleOTPChange}
                  placeholder="Enter 6-digit OTP"
                  maxLength="6"
                  required
                  disabled={loading}
                  autoComplete="one-time-code"
                />
                <small className="form-hint">
                  Enter the 6-digit OTP sent to your phone
                </small>
              </div>
              
              <button type="submit" className="btn-primary" disabled={loading || otp.length !== 6}>
                {loading ? (
                  <>
                    <i className="fas fa-spinner fa-spin"></i> Verifying...
                  </>
                ) : (
                  'Verify OTP'
                )}
              </button>
              
              <div className="otp-actions">
                <button 
                  type="button" 
                  className="btn-secondary" 
                  onClick={handleBackToPhone}
                  disabled={loading}
                >
                  <i className="fas fa-arrow-left"></i> Back to Phone Number
                </button>
                
                <button 
                  type="button" 
                  className="btn-link" 
                  onClick={handleResendOTP}
                  disabled={loading}
                >
                  <i className="fas fa-redo"></i> Resend OTP
                </button>
              </div>
            </form>
          )}

          <div className="login-footer">
            <p><a href="#"><i className="fas fa-key"></i> Forgot Password?</a></p>
            <p>
              Don't have an account? 
              <a href="/signup"><i className="fas fa-user-plus"></i> Sign Up</a>
            </p>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Login;
