import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './css/Dashboard.css';
import logoImage from '../assets/aqi.webp';
import { useAuth } from '../App';

// Lazy loading components
const LazyMap = React.lazy(() => import('./LazyMap'));
const LazyChart = React.lazy(() => import('./LazyChart'));

// --- ALL HELPER FUNCTIONS ---
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

// (Your other helper functions like getLocationName, KNOWN_LOCATIONS, etc. are correct and can be pasted here)
const parseOpenStreetMapResult = (data) => {
    if (!data || !data.address) return null;
    const addr = data.address;
    const primaryLocation = addr.village || addr.town || addr.city || addr.county || 'Unknown Location';
    return {
        display_name: `${primaryLocation}, ${addr.state || 'Kerala'}`,
        city: primaryLocation,
        state: addr.state || 'Kerala',
        country: addr.country || 'India'
    };
};

const getLocationName = async (lat, lng) => {
    // This is a simplified placeholder. Your complex getLocationName function is good.
    try {
        const response = await fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}`);
        const data = await response.json();
        return parseOpenStreetMapResult(data);
    } catch {
        return { display_name: 'Your Location', city: 'Unknown', state: 'Kerala', country: 'India' };
    }
};

function Dashboard() {
  // ===== STATE MANAGEMENT =====
  const { user, logout } = useAuth();

  const [selectedParameter, setSelectedParameter] = useState('pm25');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dashboardData, setDashboardData] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [userLocationName, setUserLocationName] = useState(null);
  const [nearestStationInfo, setNearestStationInfo] = useState(null);
  const [currentDataInfo, setCurrentDataInfo] = useState(null);
  const [locationStatus, setLocationStatus] = useState('initializing');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isLocationUpdating, setIsLocationUpdating] = useState(false);
  const [lastUpdateTime, setLastUpdateTime] = useState(new Date());
  const [healthData, setHealthData] = useState(null);
  const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);

  const navigate = useNavigate();
  const API_BASE_URL = process.env.NODE_ENV === 'production'
    ? 'https://airaware-app-gcw7.onrender.com'
    : 'http://localhost:8000';
  const abortControllerRef = useRef(null);

  // ===== FUNCTION DEFINITIONS (CORRECT ORDER) =====

  const calculateIDWInterpolation = useCallback((locationData, stations) => {
    let totalWeight = 0;
    const weightedValues = { pm25: 0, pm10: 0, so2: 0, no2: 0, co: 0, o3: 0, nh3: 0, temp: 0, hum: 0, pre: 0 };
    let weightedAqi = 0;
    Object.keys(stations).forEach(stationId => {
        const station = stations[stationId];
        const distance = calculateDistance(locationData.lat, locationData.lng, station.station_info.lat, station.station_info.lng);
        const weight = 1.0 / (Math.max(distance, 0.001) ** 2);
        totalWeight += weight;
        const averages = station.averages || {};
        Object.keys(weightedValues).forEach(param => {
            if (averages[param] !== undefined) weightedValues[param] += averages[param] * weight;
        });
        weightedAqi += (station.highest_sub_index || 0) * weight;
    });
    const interpolated_values = {};
    Object.keys(weightedValues).forEach(param => interpolated_values[param] = totalWeight > 0 ? (weightedValues[param] / totalWeight) : 0);
    const interpolated_aqi = totalWeight > 0 ? Math.round(weightedAqi / totalWeight) : 50;
    return { interpolated_values, interpolated_aqi };
  }, []);

  const processDashboardData = useCallback((data, locationData) => {
    if (!data || !data.stations) {
      console.warn('No station data received');
      return;
    }
    setDashboardData(data);
    const stations = data.stations;
    const stationIds = Object.keys(stations);
    if (locationData && stationIds.length > 0) {
      let nearestDist = Infinity, nearestId = null;
      stationIds.forEach(stationId => {
        const dist = calculateDistance(locationData.lat, locationData.lng, stations[stationId].station_info.lat, stations[stationId].station_info.lng);
        if (dist < nearestDist) { nearestDist = dist; nearestId = stationId; }
      });
      const nearestStation = stations[nearestId];
      setNearestStationInfo({ id: nearestId, name: nearestStation.station_info.name, distance: nearestDist, aqi: nearestStation.highest_sub_index || 50 });
      if (nearestDist <= 2.0) {
        const idwResult = calculateIDWInterpolation(locationData, stations);
        setCurrentDataInfo({
          method: 'location_interpolation', values: idwResult.interpolated_values, aqi: idwResult.interpolated_aqi,
          station_name: `Your Location (${userLocationName?.city || 'Current Position'})`, is_interpolated: true
        });
      } else {
        setCurrentDataInfo({
          method: 'nearest_station', values: nearestStation.averages || {}, aqi: nearestStation.highest_sub_index || 50,
          station_name: nearestStation.station_info.name, is_interpolated: false
        });
      }
    } else if (stationIds.length > 0) {
      const defaultStation = stations['lora-v1'] || stations[stationIds[0]] || {};
      setCurrentDataInfo({
        method: 'default_station', values: defaultStation.averages || {}, aqi: defaultStation.highest_sub_index || 50,
        station_name: defaultStation.station_info?.name || 'ASIET Campus Station', is_interpolated: false
      });
    }
    console.log('📊 Dashboard data processed successfully');
  }, [userLocationName, calculateIDWInterpolation]);

  const getUserLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) return reject(new Error('Geolocation not supported.'));
      setLocationStatus('detecting');
      setIsLocationUpdating(true);
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const location = { lat: position.coords.latitude, lng: position.coords.longitude, accuracy: position.coords.accuracy };
          if (isNaN(location.lat) || isNaN(location.lng)) return reject(new Error('Invalid GPS coordinates'));
          try {
            setUserLocationName(await getLocationName(location.lat, location.lng));
          } catch (nameError) {
            // Your fallback logic here
          }
          setUserLocation(location);
          setLocationStatus('gps_detected');
          setIsLocationUpdating(false);
          resolve(location);
        },
        (error) => {
          setLocationStatus('failed');
          setIsLocationUpdating(false);
          reject(error);
        },
        { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
      );
    });
  }, []);

  const fetchDashboardData = useCallback(async (locationData = null) => {
    if (!user || !user.user_id) {
      navigate('/login');
      return;
    }
    setLoading(true);
    setError(null);
    const controller = new AbortController();
    abortControllerRef.current = controller;
    try {
      const url = new URL(`${API_BASE_URL}/api/dashboard/`);
      url.searchParams.append('user_id', user.user_id);
      if (locationData) {
        url.searchParams.append('lat', locationData.lat.toString());
        url.searchParams.append('lng', locationData.lng.toString());
      }
      const response = await fetch(url, { signal: controller.signal });
      if (!response.ok) {
        const errData = await response.json();
        if (errData.redirect_to) navigate(errData.redirect_to);
        throw new Error(errData.error || `HTTP ${response.status}`);
      }
      const data = await response.json();
      setHealthData(data.health_data);
      processDashboardData(data, locationData);
      setLastUpdateTime(new Date());
    } catch (error) {
      if (error.name !== 'AbortError') setError("Failed to load dashboard. Please try again.");
    } finally {
      setLoading(false);
    }
  }, [user, navigate, API_BASE_URL, processDashboardData]);

  const handleEnableLocation = useCallback(async () => {
    try {
      const location = await getUserLocation();
      await fetchDashboardData(location);
    } catch (error) {
      console.error('Failed to get location:', error);
    }
  }, [getUserLocation, fetchDashboardData]);

  const handleParameterChange = useCallback((param) => {
    setSelectedParameter(param);
  }, []);

  const handleNavLinkClick = useCallback(() => {
    if (isMobileView) setIsMenuOpen(false);
  }, [isMobileView]);

  // ===== INITIALIZATION & MEMOIZED VALUES =====
  useEffect(() => {
    const handleResize = () => setIsMobileView(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const initializeDashboard = async () => {
      let location = null;
      try {
        location = await getUserLocation();
      } catch (e) {
        console.warn("Could not get user location on init.");
      }
      if (user) {
        await fetchDashboardData(location);
      } else if (!user) {
        // If user is not available after auth check, redirect.
        navigate('/login');
      }
    };
    initializeDashboard();
    return () => {
      if (abortControllerRef.current) abortControllerRef.current.abort();
    };
  }, [user, getUserLocation, fetchDashboardData, navigate]);

  const currentValues = useMemo(() => currentDataInfo?.values || {}, [currentDataInfo]);
  const currentAQI = useMemo(() => currentDataInfo?.aqi || 50, [currentDataInfo]);
  const aqiStatus = useMemo(() => {
    if (currentAQI <= 50) return { status: 'GOOD', color: '#10b981', class: 'good' };
    if (currentAQI <= 100) return { status: 'MODERATE', color: '#f59e0b', class: 'moderate' };
    if (currentAQI <= 150) return { status: 'UNHEALTHY', color: '#ef4444', class: 'unhealthy' };
    return { status: 'HAZARDOUS', color: '#7c2d12', class: 'hazardous' };
  }, [currentAQI]);

  const LocationStatus = useMemo(() => {
      // Your full LocationStatus component logic here
      // This can now safely use handleEnableLocation
      return <div>{locationStatus}</div>;
  }, [locationStatus, userLocationName, userLocation, nearestStationInfo, isLocationUpdating, handleEnableLocation]);

  if (loading) {
      return <div>Loading Dashboard...</div>;
  }

  return (
    <div className="dashboard-page">
      <div className="realtime-status">
        🔴 LIVE • Real-time air quality monitoring system active
      </div>

      {error && <div className="error-banner">⚠️ {error}<button onClick={() => setError(null)} className="error-close">✕</button></div>}

      <nav className="navbar">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            <img src={logoImage} alt="AQM Logo" width={isMobileView ? "32" : "40"} height={isMobileView ? "32" : "40"} />
            AirAware
          </Link>
          <div className="menu-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>{isMenuOpen ? '✕' : '☰'}</div>
          <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
            <li><Link to="/" className="nav-link" onClick={handleNavLinkClick}>🏠 Home</Link></li>
            <li><Link to="/health-assessment" className="nav-link" onClick={handleNavLinkClick}>📋 Health Update</Link></li>
            <li><Link to="/health-report" className="nav-link" onClick={handleNavLinkClick}>📄 Health Report</Link></li>
            <li><Link to="/add-family" className="nav-link" onClick={handleNavLinkClick}>👥 Add Family</Link></li>
            <li><Link to="/map" className="nav-link" onClick={handleNavLinkClick}>🗺️ Live Map</Link></li>
            <li className="user-info">👤 <span>{user ? user.name : 'User'}</span></li>
            <li><button onClick={logout} className="nav-link login-btn">🚪 Logout</button></li>
          </ul>
        </div>
      </nav>

      <div className={`alert-banner ${aqiStatus.class}`}>
        ℹ️ <span>{currentDataInfo?.station_name || 'Your Location'} AQI: {Math.round(currentAQI)} - {aqiStatus.status}</span>
      </div>

      <main className="main-container">
        <header className="welcome-header">
          <div className="welcome-content">
            <div className="welcome-info">
              <div className="user-avatar">👤</div>
              <div className="welcome-text">
                <h1>Welcome, {user ? user.name : 'Guest'}!</h1>
                {LocationStatus}
                {healthData && (
                  <div className="health-status-inline">
                    <span className="health-label">Your Health Risk: </span>
                    <span className={`health-level ${healthData.risk_level.toLowerCase()}`}>{healthData.risk_level}</span>
                  </div>
                )}
              </div>
            </div>
            <div className="aqi-overview">
              <div className="aqi-value" style={{ color: aqiStatus.color }}>{Math.round(currentAQI)}</div>
              <div className="aqi-status">{currentDataInfo?.is_interpolated ? 'Your Location AQI' : 'Nearest Station AQI'}</div>
              <div className="last-updated">Updated: {lastUpdateTime.toLocaleTimeString()}</div>
            </div>
          </div>
        </header>

        <section className="metrics-section">
          <h2 className="section-title">🌬️ Air Quality Readings</h2>
          <div className="metrics-grid">
            {['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'].map(key => (
              <div key={key} className="metric-card">
                <div className="metric-value">{currentValues[key]?.toFixed(1) || 'N/A'}</div>
                <div className="metric-label">{key.toUpperCase()}</div>
              </div>
            ))}
          </div>
        </section>

        <section className="dashboard-grid">
          <div className="dashboard-card map-card">
            <h3 className="card-title">🗺️ Live Sensor Network</h3>
            <div className="map-container">
              <React.Suspense fallback={<div className="map-fallback">Loading Map...</div>}>
                <LazyMap userLocation={userLocation} stations={dashboardData?.stations} />
              </React.Suspense>
            </div>
          </div>
          <div className="dashboard-card forecast-card">
            <h3 className="card-title">📊 4-Day Forecast</h3>
            <div className="forecast-controls">
              {['pm25', 'pm10', 'no2', 'o3'].map(p => 
                <button key={p} className={`param-btn ${selectedParameter === p ? 'active' : ''}`} onClick={() => handleParameterChange(p)}>
                  {p.toUpperCase()}
                </button>
              )}
            </div>
            <div className="chart-section">
              <React.Suspense fallback={<div className="chart-fallback">Loading Chart...</div>}>
                <LazyChart forecastData={dashboardData?.forecasts?.[nearestStationInfo?.id || 'lora-v1']} selectedParameter={selectedParameter} />
              </React.Suspense>
            </div>
          </div>
        </section>

        {healthData && (
          <section className="health-section">
            <h2 className="section-title">❤️ Health Status Monitor</h2>
            <div className="health-content">
              <div className="health-display">
                <div className="health-score-circle"><div className="health-score-value">{healthData.score}</div><div className="health-score-label">Score</div></div>
                <div className="health-details">
                  <div className={`risk-level ${healthData.risk_level.toLowerCase()}`}>{healthData.risk_level} Risk</div>
                  <div className="health-recommendations">
                    {healthData.recommendations?.map((rec, i) => <div key={i} className="recommendation">• {rec}</div>)}
                  </div>
                </div>
              </div>
            </div>
          </section>
        )}
      </main>

      <footer className="footer">
        <div className="footer-container">
          <div className="footer-content">
            <div className="footer-section">
              <h4>AirAware Kerala</h4>
              <p>Smart Air Quality Monitoring</p>
            </div>
            <div className="footer-section">
              <h4>Quick Links</h4>
              <ul>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/health-report">Health Report</Link></li>
              </ul>
            </div>
            <div className="footer-section">
              <h4>Contact</h4>
              <p>Adi Shankara Institute, Kalady</p>
              <p>Email: aiiot@adishankara.ac.in</p>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 AirAware Kerala. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Dashboard;
