import React, { useState, useEffect, useCallback, useMemo, Suspense } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './css/Dashboard.css';
import logoImage from '../assets/aqi.webp';

// --- LAZY-LOADED COMPONENTS ---
const LazyMap = React.lazy(() => import('./LazyMap'));
const LazyChart = React.lazy(() => import('./LazyChart'));

// --- HELPER FUNCTIONS ---
const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // km
    const dLat = (lat2 - lat1) * Math.PI / 180, dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) ** 2 + Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
};

const getLocationName = async (lat, lng) => {
    try {
        const response = await fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&zoom=14&addressdetails=1`);
        const data = await response.json();
        const addr = data.address;
        const city = addr.city || addr.town || addr.village || addr.state_district || 'Your Location';
        return { display_name: `${city}, ${addr.state}`, city };
    } catch {
        return { display_name: 'Your Location', city: 'Current Area' };
    }
};

function Dashboard() {
    // --- STATE MANAGEMENT ---
    const [username, setUsername] = useState(null);
    const [dashboardData, setDashboardData] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [userLocationName, setUserLocationName] = useState(null);
    const [nearestStationInfo, setNearestStationInfo] = useState(null);
    const [currentDataInfo, setCurrentDataInfo] = useState(null);
    const [locationStatus, setLocationStatus] = useState('initializing');
    const [loading, setLoading] = useState(true);
    const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 768);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [lastUpdateTime, setLastUpdateTime] = useState(new Date());
    const [healthData, setHealthData] = useState(null);
    const [selectedParameter, setSelectedParameter] = useState('pm25');

    const navigate = useNavigate();
    const API_BASE_URL = process.env.NODE_ENV === 'production' 
        ? 'https://airaware-app-gcw7.onrender.com' 
        : 'http://localhost:8000';

    // --- SESSION & AUTHENTICATION CHECK ---
    useEffect(() => {
        try {
            const user = JSON.parse(localStorage.getItem('user'));
            const isLoggedIn = localStorage.getItem('isLoggedIn');
            
            if (isLoggedIn === 'true' && user && user.name) {
                setUsername(user.name);
            } else {
                navigate('/login');
            }
        } catch (error) {
            navigate('/login');
        }
    }, [navigate]);

    // --- RESPONSIVE UI HELPER ---
    useEffect(() => {
        const handleResize = () => setIsMobileView(window.innerWidth <= 768);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);
    
    // --- DATA CALCULATION ---
    const calculateSmartEstimation = useCallback((locationData, stations) => {
        const realStationIds = ['lora-v1', 'loradev2'];
        const realStations = {};
        realStationIds.forEach(id => {
            if (stations[id]) realStations[id] = stations[id];
        });

        if (Object.keys(realStations).length === 0) return { values: {}, aqi: 50 };

        let totalWeight = 0;
        const weightedValues = { pm25: 0, pm10: 0, so2: 0, no2: 0, co: 0, o3: 0, nh3: 0, temp: 0, hum: 0, pre: 0 };
        let weightedAqi = 0;

        Object.values(realStations).forEach(station => {
            const distance = calculateDistance(locationData.lat, locationData.lng, station.station_info.lat, station.station_info.lng);
            const weight = 1.0 / (Math.max(distance, 0.001) ** 2);
            totalWeight += weight;
            Object.keys(weightedValues).forEach(param => {
                if (station.averages?.[param] !== undefined) weightedValues[param] += station.averages[param] * weight;
            });
            weightedAqi += (station.highest_sub_index || 0) * weight;
        });

        const estimatedValues = {};
        Object.keys(weightedValues).forEach(param => {
            estimatedValues[param] = totalWeight > 0 ? parseFloat((weightedValues[param] / totalWeight).toFixed(2)) : 0;
        });
        const estimatedAqi = totalWeight > 0 ? Math.round(weightedAqi / totalWeight) : 50;
        
        return { values: estimatedValues, aqi: estimatedAqi };
    }, []);

    const processDashboardData = useCallback((data, locationData) => {
        if (!data?.stations) {
            const defaultStation = { station_info: {name: 'ASIET Campus Station'}, averages: {}, highest_sub_index: 50 };
             setCurrentDataInfo({
                source: 'default',
                explanation: 'Connecting to sensor network...',
                values: defaultStation.averages,
                aqi: defaultStation.highest_sub_index,
                station_name: defaultStation.station_info.name,
                is_estimated: false,
                distance_message: 'üìç Kalady Area Data'
            });
            return;
        }

        setDashboardData(data);
        const { stations } = data;
        
        if (locationData) {
            const estimation = calculateSmartEstimation(locationData, stations);
            const nearestRealStation = stations['lora-v1']; 
            const distanceToNearest = calculateDistance(locationData.lat, locationData.lng, nearestRealStation.station_info.lat, nearestRealStation.station_info.lng);

            setNearestStationInfo({
                id: 'lora-v1', 
                name: nearestRealStation.station_info.name,
                distance: distanceToNearest
            });

            setCurrentDataInfo({
                source: 'user_location',
                explanation: `Showing air quality calculated for your location based on our real sensor network.`,
                values: estimation.values,
                aqi: estimation.aqi,
                station_name: userLocationName?.city || 'Your Location',
                is_estimated: true,
                distance_message: distanceToNearest < 5 ? `üéØ High-Accuracy Data` : `üåç Regional Estimation`
            });
        } else {
            const defaultStation = stations['lora-v1'];
            setCurrentDataInfo({
                source: 'default',
                explanation: 'Enable location services for personalized data. Showing information from our primary monitoring station in Kalady.',
                values: defaultStation.averages || {},
                aqi: defaultStation.highest_sub_index || 50,
                station_name: defaultStation.station_info.name,
                is_estimated: false,
                distance_message: 'üìç Kalady Area Data'
            });
        }
        if (data.health_data) setHealthData(data.health_data);
        setLastUpdateTime(new Date());

    }, [userLocationName, calculateSmartEstimation]);
    
    const fetchDashboardData = useCallback(async (location) => {
        if (!username) return; 
        
        setLoading(true);
        try {
            const url = new URL(`${API_BASE_URL}/api/dashboard/`);
            url.searchParams.append('username', username);
            const response = await fetch(url);
            if (!response.ok) {
                if(response.status === 404) navigate('/health-assessment');
                throw new Error('Failed to fetch data');
            }
            const data = await response.json();
            processDashboardData(data, location);
        } catch (error) {
            console.error("Fetch error:", error);
            processDashboardData(null, location);
        } finally {
            setLoading(false);
        }
    }, [username, API_BASE_URL, processDashboardData, navigate]);

    const getUserLocation = useCallback(async () => {
        setLocationStatus('detecting');
        try {
            const position = await new Promise((resolve, reject) => 
                navigator.geolocation.getCurrentPosition(resolve, reject, { timeout: 10000, enableHighAccuracy: true })
            );
            const location = { lat: position.coords.latitude, lng: position.coords.longitude };
            setUserLocation(location);
            const name = await getLocationName(location.lat, location.lng);
            setUserLocationName(name);
            setLocationStatus('gps_detected');
            return location;
        } catch (error) {
            console.warn("Location detection failed:", error.message);
            setLocationStatus('failed');
            return null;
        }
    }, []);

    // --- INITIALIZATION EFFECT ---
    useEffect(() => {
        if (username) {
            const initialize = async () => {
                setLoading(true);
                const location = await getUserLocation();
                await fetchDashboardData(location);
                setLoading(false);
            };
            initialize();
        }
    }, [username, getUserLocation, fetchDashboardData]);
    
    // --- MEMOIZED VALUES FOR PERFORMANCE ---
    const aqiStatus = useMemo(() => {
        const aqi = currentDataInfo?.aqi || 0;
        if (aqi <= 50) return { status: 'GOOD', class: 'good', color: '#10b981' };
        if (aqi <= 100) return { status: 'MODERATE', class: 'moderate', color: '#f59e0b' };
        if (aqi <= 150) return { status: 'UNHEALTHY', class: 'unhealthy', color: '#ef4444' };
        return { status: 'HAZARDOUS', class: 'hazardous', color: '#7c2d12' };
    }, [currentDataInfo]);

    const handleLogout = useCallback(() => {
        localStorage.clear();
        navigate('/login');
    }, [navigate]);

    const handleRefresh = useCallback(() => {
        if (username) {
            const refresh = async () => {
                const location = await getUserLocation();
                await fetchDashboardData(location);
            };
            refresh();
        }
    }, [username, getUserLocation, fetchDashboardData]);

    const formatValue = useCallback((value, metric) => {
        const numValue = parseFloat(value);
        if (isNaN(numValue)) return '0';
        return metric === 'co' ? numValue.toFixed(1) : Math.round(numValue);
    }, []);

    // --- RENDER LOGIC ---
    if (loading || !currentDataInfo) {
        return <div className="loading-container"><h2>Initializing AirAware Dashboard...</h2><div className="loading-spinner"></div></div>;
    }
    
    return (
        <div className="dashboard-page">
            <div className="realtime-status">üî¥ LIVE ‚Ä¢ Real-time air quality monitoring system active</div>

            <nav className="navbar">
                <div className="navbar-content">
                    <Link to="/" className="navbar-brand">
                        <img src={logoImage} alt="AQM Logo" width={isMobileView ? "32" : "40"} height={isMobileView ? "32" : "40"} />
                        AirAware
                    </Link>
                    <div className="menu-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>{isMenuOpen ? '‚úï' : '‚ò∞'}</div>
                    <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
                        <li><Link to="/" className="nav-link" onClick={() => setIsMenuOpen(false)}>üè† Home</Link></li>
                        <li><Link to="/health-assessment" className="nav-link" onClick={() => setIsMenuOpen(false)}>üìã Health Update</Link></li>
                        <li><Link to="/health-report" className="nav-link" onClick={() => setIsMenuOpen(false)}>üìÑ Health Report</Link></li>
                        <li><Link to="/add-family" className="nav-link" onClick={() => setIsMenuOpen(false)}>üë• Add Family</Link></li>
                        <li><Link to="/map" className="nav-link" onClick={() => setIsMenuOpen(false)}>üó∫Ô∏è Live Map</Link></li>
                        <li className="user-info">üë§ <span>{username}</span></li>
                        <li><button onClick={handleLogout} className="nav-link login-btn">üö™ Logout</button></li>
                    </ul>
                </div>
            </nav>

            <div className={`alert-banner ${aqiStatus.class}`}>
                ‚ÑπÔ∏è <span>{currentDataInfo.station_name} AQI: {Math.round(currentDataInfo.aqi)} - {aqiStatus.status}</span>
            </div>

            <div className="main-container">
                <div className="welcome-header">
                    <div className="welcome-content">
                        <div className="welcome-info">
                             <div className="user-avatar">üë§</div>
                            <div className="welcome-text">
                                <h1>Welcome, {username}!</h1>
                                <div className="location-info">
                                    {locationStatus === 'gps_detected' && `üìç ${userLocationName?.display_name}`}
                                    {locationStatus === 'detecting' && `üîç Detecting Location...`}
                                    {locationStatus === 'failed' && `üìç Showing Regional Data`}
                                </div>
                            </div>
                        </div>
                        <div className="aqi-overview">
                            <div className="aqi-value" style={{color: aqiStatus.color}}>{Math.round(currentDataInfo.aqi)}</div>
                            <div className="aqi-status">{currentDataInfo.is_estimated ? 'Calculated AQI' : 'Station AQI'}</div>
                        </div>
                    </div>
                </div>

                <div className="location-context-banner-horizontal">
                    <div className="location-main-content">
                        <div className="location-icon-section">
                            <div className="location-badge-large">{currentDataInfo.distance_message}</div>
                        </div>
                        <div className="location-details-section">
                            <h3>{currentDataInfo.explanation}</h3>
                        </div>
                    </div>
                </div>
                
                <div className="action-buttons">
                    <button onClick={handleRefresh} className="action-btn secondary" disabled={loading}>
                        {loading ? 'üîÑ Refreshing...' : 'üîÑ Refresh Data'}
                    </button>
                </div>

                <div className="metrics-section">
                    <h2 className="section-title">üå¨Ô∏è Air Quality Details</h2>
                    <div className="metrics-grid">
                        {['pm25', 'pm10', 'so2', 'no2', 'co', 'o3', 'nh3'].map(key => (
                            <div key={key} className="metric-card">
                                <div className="metric-value">{formatValue(currentDataInfo.values[key] || 0, key)}</div>
                                <div className="metric-label">{key.toUpperCase()}</div>
                            </div>
                        ))}
                    </div>
                </div>

                <div className="dashboard-grid">
                    <div id="map-section" className="dashboard-card map-card">
                        <div className="card-header">
                            <h3 className="card-title">üó∫Ô∏è Live Sensor Network</h3>
                        </div>
                        <div className="map-container">
                            <Suspense fallback={<div className="map-fallback"><p>Loading Map...</p></div>}>
                                <LazyMap userLocation={userLocation} stations={dashboardData?.stations} />
                            </Suspense>
                        </div>
                        <div className="map-info">
                            <span className="status-indicator">üî¥ LIVE ‚Ä¢ Sensor Network Active</span>
                            {currentDataInfo.is_estimated && <span className="smart-badge">üéØ {currentDataInfo.distance_message}</span>}
                        </div>
                    </div>
                    
                    <div className="dashboard-card forecast-card">
                        <div className="card-header">
                          <h3 className="card-title">üìä 4-Day Air Quality Forecast</h3>
                        </div>
                        <div className="forecast-content">
                          <div className="chart-section">
                            <Suspense fallback={<div className="chart-fallback"><p>Loading Chart...</p></div>}>
                              <LazyChart
                                forecastData={dashboardData?.forecasts?.[nearestStationInfo?.id || 'lora-v1']}
                                selectedParameter={selectedParameter}
                              />
                            </Suspense>
                          </div>
                          <div className="forecast-table-section">
                            <table className="forecast-table">
                              <thead>
                                <tr>
                                    <th>Day</th>
                                    <th>Max {selectedParameter.toUpperCase()}</th>
                                    <th>Unit</th>
                                </tr>
                              </thead>
                              <tbody>
                                {(dashboardData?.forecasts?.[nearestStationInfo?.id || 'lora-v1'] || []).map((item, index) => (
                                  <tr key={index}>
                                    <td>{item.day}</td>
                                    <td>{item[`${selectedParameter}_max`] || 'N/A'}</td>
                                    <td>{selectedParameter === 'co' ? 'mg/m¬≥' : '¬µg/m¬≥'}</td>
                                  </tr>
                                ))}
                              </tbody>
                            </table>
                          </div>
                        </div>
                    </div>
                </div>
                 {healthData && (
                  <div className="health-section">
                    <div className="health-content">
                      <div className="health-chart-section">
                        <div className="card-header">
                          <h3 className="card-title">‚ù§Ô∏è Health Status Monitor</h3>
                        </div>
                        <div className="health-display">
                          <div className="health-score-circle">
                            <div className="health-score-value">{healthData.score}</div>
                            <div className="health-score-label">Health Score</div>
                          </div>
                          <div className="health-details">
                            <div className={`risk-level ${healthData.risk_level.toLowerCase()}`}>
                              {healthData.risk_level} Risk
                            </div>
                            <div className="health-recommendations">
                              {healthData.recommendations?.map((rec, index) => (
                                <div key={index} className="recommendation">‚Ä¢ {rec}</div>
                              ))}
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="health-advisory">
                        <h3>
                          <i className="fas fa-shield-alt"></i>
                           Health Advisory
                        </h3>
                        <p>
                          People with asthma, heart disease, older adults, and young children are more 
                          susceptible to air pollution. Please follow health advisories closely and seek 
                          medical attention if you experience any adverse symptoms.
                        </p>
                        {healthData.risk_level === 'High' && (
                          <div className="emergency-contact">
                            <strong>Emergency Contact:</strong> Kerala Pollution Control Board ‚Äì 0471-2418566
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                )}
                 <div className="weather-section">
                   <h2 className="section-title">üå§Ô∏è Weather Conditions</h2>
                   <div className="weather-grid">
                     <div className="weather-card">
                       <div className="weather-icon">üå°Ô∏è</div>
                       <div className="weather-value">{formatValue(currentDataInfo.values.temp || 28, 'temp')}¬∞C</div>
                       <div className="weather-label">Temperature</div>
                     </div>
                     <div className="weather-card">
                       <div className="weather-icon">üíß</div>
                       <div className="weather-value">{formatValue(currentDataInfo.values.hum || 65, 'hum')}%</div>
                       <div className="weather-label">Humidity</div>
                     </div>
                     <div className="weather-card">
                       <div className="weather-icon">üìè</div>
                       <div className="weather-value">{formatValue(currentDataInfo.values.pre || 1013, 'pre')} hPa</div>
                       <div className="weather-label">Atmospheric Pressure</div>
                     </div>
                   </div>
                 </div>

                 <div className="quick-actions">
                   <h3>‚ö° Quick Actions</h3>
                   <div className="quick-actions-grid">
                     <button 
                       onClick={handleRefresh}
                       className="quick-action-btn"
                       disabled={loading}
                     >
                       <div className="quick-action-icon">üîÑ</div>
                       <div className="quick-action-text">
                         <div className="quick-action-title">
                           {loading ? 'Refreshing...' : 'Refresh Data'}
                         </div>
                         <div className="quick-action-desc">Get latest air quality readings</div>
                       </div>
                     </button>
                     
                     <button 
                       onClick={() => document.getElementById('map-section')?.scrollIntoView({ behavior: 'smooth' })}
                       className="quick-action-btn"
                     >
                       <div className="quick-action-icon">üó∫Ô∏è</div>
                       <div className="quick-action-text">
                         <div className="quick-action-title">View Map</div>
                         <div className="quick-action-desc">See sensor locations and coverage</div>
                       </div>
                     </button>
                     
                     <button 
                       onClick={() => navigate('/health-assessment')}
                       className="quick-action-btn"
                     >
                       <div className="quick-action-icon">üìã</div>
                       <div className="quick-action-text">
                         <div className="quick-action-title">Health Assessment</div>
                         <div className="quick-action-desc">Update your health profile</div>
                       </div>
                     </button>
                   </div>
                 </div>
            </div>

            <footer className="footer">
                <div className="footer-container">
                    <div className="footer-content">
                        <div className="footer-section">
                            <h4>AirAware Kerala</h4>
                            <p>Smart Air Quality Monitoring</p>
                            <div className="social-links">
                                <a href="/" className="social-link">üìò</a>
                                <a href="/" className="social-link">üê¶</a>
                                <a href="/" className="social-link">üíº</a>
                            </div>
                        </div>
                        <div className="footer-section">
                            <h4>Quick Links</h4>
                            <ul>
                                <li><Link to="/dashboard">Dashboard</Link></li>
                                <li><Link to="/map">Live Map</Link></li>
                                <li><Link to="/health-report">Health Report</Link></li>
                            </ul>
                        </div>
                        <div className="footer-section">
                            <h4>Contact</h4>
                            <p>ASIET, Kalady, Ernakulam</p>
                            <p>Email: aiiot@adishankara.ac.in</p>
                        </div>
                    </div>
                    <div className="footer-bottom">
                        <p>&copy; 2025 AirAware. All rights reserved.</p>
                    </div>
                </div>
            </footer>
        </div>
    );
}

export default Dashboard;
